import React, { useState, useEffect, useCallback } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link, useNavigate } from 'react-router-dom';
import { Toaster } from '@/components/ui/toaster';
import LanguageSwitcher from '@/components/LanguageSwitcher';
import LanguageSelectionModal from '@/components/LanguageSelectionModal';
import { TelegramProvider } from '@/contexts/TelegramContext';
import { getLocaleString } from '@/lib/locales';
import EpisodesPage from '@/pages/EpisodesPage';
import PlayerPage from '@/pages/PlayerPage';
import ManagePage from '@/pages/ManagePage'; 
import NotFoundPage from '@/pages/NotFoundPage';
import DeepSearchPage from '@/pages/DeepSearchPage';
import { supabase } from '@/lib/supabaseClient';
import { TooltipProvider } from '@/components/ui/tooltip';


const FooterContent = ({ currentLanguage, onLanguageSelect }) => {
  const navigate = useNavigate();

  const handleLanguageSwitchInPlayer = async (newLang) => {
    const pathParts = window.location.pathname.split('/');
    if (pathParts.length === 3 && pathParts[1] === 'episode') {
      const currentSlug = pathParts[2];

      const { data: currentEpisode, error: currentEpError } = await supabase
        .from('episodes')
        .select('date, lang, slug')
        .eq('slug', currentSlug)
        .single();

      if (currentEpError || !currentEpisode) {
        console.error("Error fetching current episode details for lang switch:", currentEpError);
        onLanguageSelect(newLang); 
        return;
      }

      const datePartFromSlug = currentEpisode.slug.substring(0, currentEpisode.slug.lastIndexOf('_'));
      const targetSlugPart = `${datePartFromSlug}_${newLang}`;

      const { data: targetEpisode, error: targetEpError } = await supabase
        .from('episodes')
        .select('slug')
        .eq('date', currentEpisode.date) 
        .eq('lang', newLang) 
        .eq('slug', targetSlugPart)
        .single();

      if (!targetEpError && targetEpisode) {
        onLanguageSelect(newLang);
        navigate(`/episode/${targetEpisode.slug}${window.location.hash}`);
      } else {
         const { data: anyEpisodeSameDate, error: anyEpError } = await supabase
            .from('episodes')
            .select('slug')
            .eq('date', currentEpisode.date)
            .eq('lang', newLang)
            .limit(1)
            .single();

        if(!anyEpError && anyEpisodeSameDate){
            onLanguageSelect(newLang);
            navigate(`/episode/${anyEpisodeSameDate.slug}${window.location.hash}`);
        } else {
            console.warn("No equivalent episode found for the new language with the same date. Defaulting to language switch only.");
            onLanguageSelect(newLang);
        }
      }
    } else {
      onLanguageSelect(newLang);
    }
  };

  return (
    <footer className="py-3 sm:py-4 text-center text-xs sm:text-sm text-white/60 flex flex-col items-center gap-2">
      <div className="flex gap-2 items-center">
        {currentLanguage && <LanguageSwitcher currentLanguage={currentLanguage} onLanguageChange={handleLanguageSwitchInPlayer} />}
      </div>
    </footer>
  );
}


function App() {
  const [currentLanguage, setCurrentLanguage] = useState(localStorage.getItem('podcastLang') || null);
  const [showLangModal, setShowLangModal] = useState(!currentLanguage);
  const [authLoading, setAuthLoading] = useState(true);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const checkUser = async () => {
      const { data: { session } } = await supabase.auth.getSession();
      const currentUser = session?.user ?? null;
      setUser(currentUser);
      setAuthLoading(false);
    };

    checkUser();

    const { data: authListener } = supabase.auth.onAuthStateChange((_event, session) => {
      const currentUser = session?.user ?? null;
      setUser(currentUser);
    });

    if (currentLanguage) {
      localStorage.setItem('podcastLang', currentLanguage);
      setShowLangModal(false);
    } else {
      setShowLangModal(true);
    }

    return () => {
      authListener.subscription.unsubscribe();
    };
  }, [currentLanguage]);

  const handleLanguageSelect = useCallback((lang) => {
    setCurrentLanguage(lang);
    localStorage.setItem('podcastLang', lang);
    setShowLangModal(false);
  }, []);

  if (authLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 text-white flex items-center justify-center">
        <p>{getLocaleString('loading', currentLanguage || 'ru')}</p>
      </div>
    );
  }

  if (showLangModal) {
    return (
      <TelegramProvider>
         <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 text-white flex flex-col">
            <LanguageSelectionModal onLanguageSelect={handleLanguageSelect} currentLanguage={currentLanguage || 'ru'} />
            <Toaster />
          </div>
      </TelegramProvider>
    );
  }

  return (
    <TelegramProvider>
      <TooltipProvider>
        <Router>
          <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 text-white flex flex-col">
            <main className="flex-grow w-full">
              <Routes>
                <Route path="/" element={<Navigate to="/episodes" replace />} />
                <Route path="/episodes" element={<EpisodesPage currentLanguage={currentLanguage} />} />
                <Route path="/episode/:episodeSlug" element={<PlayerPage currentLanguage={currentLanguage} user={user} />} />
                <Route path="/manage" element={<ManagePage currentLanguage={currentLanguage} />} />
                <Route path="/deep-search" element={<DeepSearchPage currentLanguage={currentLanguage} />} />
                <Route path="*" element={<NotFoundPage currentLanguage={currentLanguage} />} />
              </Routes>
            </main>

            <FooterContent 
              currentLanguage={currentLanguage} 
              onLanguageSelect={handleLanguageSelect}
            />

            <Toaster />
          </div>
        </Router>
      </TooltipProvider>
    </TelegramProvider>
  );
}

export default App;

import React, { useState, useEffect } from 'react';
import { Download, Trash2, HardDrive, Wifi, WifiOff, CheckCircle, AlertCircle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { getLocaleString } from '@/lib/locales';
import audioCacheService from '@/lib/audioCacheService';

const AudioCacheManager = ({ 
  currentLanguage = 'ru', 
  episodeData = null,
  showInline = false,
  className = ""
}) => {
  const [cacheStats, setCacheStats] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [downloadProgress, setDownloadProgress] = useState({});
  const [isCached, setIsCached] = useState(false);
  const [isDownloading, setIsDownloading] = useState(false);

  useEffect(() => {
    loadCacheStats();

    if (episodeData?.audio_url) {
      checkIfCached();
    }

    // –ü–æ–¥–ø–∏—Å—ã–≤–∞–µ–º—Å—è –Ω–∞ —Å–æ–±—ã—Ç–∏—è –∑–∞–≥—Ä—É–∑–∫–∏
    const unsubscribeDownload = audioCacheService.onDownloadProgress((event, data) => {
      if (episodeData && data.episodeSlug === episodeData.slug) {
        switch (event) {
          case 'download_start':
            setIsDownloading(true);
            setDownloadProgress({ progress: 0, loaded: 0, total: 0 });
            break;
          case 'download_progress':
            setDownloadProgress({
              progress: data.progress,
              loaded: data.loaded,
              total: data.total
            });
            break;
          case 'download_complete':
            setIsDownloading(false);
            setIsCached(true);
            setDownloadProgress({});
            loadCacheStats(); // –û–±–Ω–æ–≤–ª—è–µ–º —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫—É
            break;
          case 'download_error':
            setIsDownloading(false);
            setDownloadProgress({});
            break;
        }
      }
    });

    return () => {
      unsubscribeDownload();
    };
  }, [episodeData]);

  const loadCacheStats = async () => {
    try {
      const stats = await audioCacheService.getCacheStats();
      setCacheStats(stats);
    } catch (error) {
      console.error('Error loading cache stats:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const checkIfCached = async () => {
    if (!episodeData?.audio_url) return;

    try {
      const cached = await audioCacheService.isAudioCached(episodeData.audio_url);
      setIsCached(cached);
    } catch (error) {
      console.error('Error checking cache status:', error);
    }
  };

  const handleDownloadEpisode = async () => {
    if (!episodeData?.audio_url || isDownloading) return;

    try {
      await audioCacheService.cacheAudio(episodeData.audio_url, episodeData.slug);
    } catch (error) {
      console.error('Error downloading episode:', error);
    }
  };

  const handleRemoveEpisode = async () => {
    if (!episodeData?.audio_url) return;

    try {
      await audioCacheService.removeAudioFromCache(episodeData.audio_url);
      setIsCached(false);
      loadCacheStats();
    } catch (error) {
      console.error('Error removing episode from cache:', error);
    }
  };

  const handleClearCache = async () => {
    try {
      await audioCacheService.clearCache();
      setIsCached(false);
      loadCacheStats();
    } catch (error) {
      console.error('Error clearing cache:', error);
    }
  };

  const formatBytes = (bytes) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  // –í—Å—Ç—Ä–æ–µ–Ω–Ω—ã–π —Ä–µ–∂–∏–º –¥–ª—è –æ—Ç–¥–µ–ª—å–Ω–æ–≥–æ —ç–ø–∏–∑–æ–¥–∞
  if (showInline && episodeData) {
    return (
      <div className={`flex items-center gap-2 ${className}`}>
        {isDownloading ? (
          <div className="flex items-center gap-2">
            <Download className="h-4 w-4 text-blue-500 animate-pulse" />
            <div className="flex flex-col gap-1 min-w-[100px]">
              <div className="text-xs text-slate-400">
                {downloadProgress.progress || 0}%
              </div>
              <Progress 
                value={downloadProgress.progress || 0} 
                className="h-1"
              />
            </div>
          </div>
        ) : isCached ? (
          <div className="flex items-center gap-2">
            <CheckCircle className="h-4 w-4 text-green-500" />
            <span className="text-xs text-green-400">
              {getLocaleString('cached', currentLanguage) || '–ö–µ—à–∏—Ä–æ–≤–∞–Ω–æ'}
            </span>
            <Button
              variant="ghost"
              size="sm"
              onClick={handleRemoveEpisode}
              className="h-6 w-6 p-0 text-red-400 hover:text-red-300"
            >
              <Trash2 className="h-3 w-3" />
            </Button>
          </div>
        ) : (
          <Button
            variant="ghost"
            size="sm"
            onClick={handleDownloadEpisode}
            className="flex items-center gap-1 h-6 px-2 text-blue-400 hover:text-blue-300"
          >
            <Download className="h-3 w-3" />
            <span className="text-xs">
              {getLocaleString('cache', currentLanguage) || '–ö–µ—à–∏—Ä–æ–≤–∞—Ç—å'}
            </span>
          </Button>
        )}
      </div>
    );
  }

  // –ü–æ–ª–Ω—ã–π —Ä–µ–∂–∏–º —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –∫–µ—à–µ–º
  if (isLoading) {
    return (
      <div className={`p-4 bg-slate-800 rounded-lg ${className}`}>
        <div className="animate-pulse">
          <div className="h-4 bg-slate-700 rounded w-1/3 mb-2"></div>
          <div className="h-2 bg-slate-700 rounded w-full mb-4"></div>
          <div className="h-8 bg-slate-700 rounded w-1/4"></div>
        </div>
      </div>
    );
  }

  return (
    <div className={`p-4 bg-slate-800 rounded-lg space-y-4 ${className}`}>
      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-medium text-white flex items-center gap-2">
          <HardDrive className="h-5 w-5" />
          {getLocaleString('audioCache', currentLanguage) || '–ö–µ—à –∞—É–¥–∏–æ'}
        </h3>
        <div className="flex items-center gap-2">
          {navigator.onLine ? (
            <Wifi className="h-4 w-4 text-green-500" />
          ) : (
            <WifiOff className="h-4 w-4 text-red-500" />
          )}
        </div>
      </div>

      {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è */}
      {cacheStats && (
        <div className="space-y-3">
          <div className="flex justify-between text-sm">
            <span className="text-slate-400">
              {getLocaleString('cacheUsage', currentLanguage) || '–ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –∫–µ—à–∞'}
            </span>
            <span className="text-white">
              {formatBytes(cacheStats.totalSize)} / {formatBytes(cacheStats.maxSize)}
            </span>
          </div>

          <Progress 
            value={cacheStats.usagePercentage} 
            className="h-2"
          />

          <div className="flex justify-between text-xs text-slate-500">
            <span>
              {cacheStats.fileCount} {getLocaleString('files', currentLanguage) || '—Ñ–∞–π–ª–æ–≤'}
            </span>
            <span>
              {cacheStats.usagePercentage}% {getLocaleString('used', currentLanguage) || '–∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–æ'}
            </span>
          </div>
        </div>
      )}

      {/* –î–µ–π—Å—Ç–≤–∏—è –¥–ª—è —Ç–µ–∫—É—â–µ–≥–æ —ç–ø–∏–∑–æ–¥–∞ */}
      {episodeData && (
        <div className="border-t border-slate-700 pt-4">
          <div className="flex items-center justify-between mb-3">
            <div>
              <div className="text-sm font-medium text-white">
                {episodeData.title}
              </div>
              <div className="text-xs text-slate-400">
                {getLocaleString('currentEpisode', currentLanguage) || '–¢–µ–∫—É—â–∏–π —ç–ø–∏–∑–æ–¥'}
              </div>
            </div>
            <div className="flex items-center gap-1">
              {isCached ? (
                <CheckCircle className="h-4 w-4 text-green-500" />
              ) : (
                <AlertCircle className="h-4 w-4 text-yellow-500" />
              )}
            </div>
          </div>

          {isDownloading ? (
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span className="text-slate-400">
                  {getLocaleString('downloading', currentLanguage) || '–ó–∞–≥—Ä—É–∑–∫–∞...'}
                </span>
                <span className="text-blue-400">
                  {downloadProgress.progress || 0}%
                </span>
              </div>
              <Progress 
                value={downloadProgress.progress || 0} 
                className="h-2"
              />
              {downloadProgress.total > 0 && (
                <div className="text-xs text-slate-500 text-center">
                  {formatBytes(downloadProgress.loaded)} / {formatBytes(downloadProgress.total)}
                </div>
              )}
            </div>
          ) : (
            <div className="flex gap-2">
              {isCached ? (
                <Button
                  variant="destructive"
                  size="sm"
                  onClick={handleRemoveEpisode}
                  className="flex-1"
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  {getLocaleString('removeFromCache', currentLanguage) || '–£–¥–∞–ª–∏—Ç—å –∏–∑ –∫–µ—à–∞'}
                </Button>
              ) : (
                <Button
                  variant="default"
                  size="sm"
                  onClick={handleDownloadEpisode}
                  className="flex-1"
                  disabled={!navigator.onLine}
                >
                  <Download className="h-4 w-4 mr-2" />
                  {getLocaleString('cacheForOffline', currentLanguage) || '–ö–µ—à–∏—Ä–æ–≤–∞—Ç—å –¥–ª—è –æ—Ñ–ª–∞–π–Ω'}
                </Button>
              )}
            </div>
          )}
        </div>
      )}

      {/* –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –∫–µ—à–µ–º */}
      <div className="border-t border-slate-700 pt-4">
        <div className="flex justify-between items-center">
          <div>
            <div className="text-sm font-medium text-white">
              {getLocaleString('cacheManagement', currentLanguage) || '–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –∫–µ—à–µ–º'}
            </div>
            <div className="text-xs text-slate-400">
              {getLocaleString('manageStoredAudio', currentLanguage) || '–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–Ω—ã–º–∏ –∞—É–¥–∏–æ—Ñ–∞–π–ª–∞–º–∏'}
            </div>
          </div>
          <Button
            variant="outline"
            size="sm"
            onClick={handleClearCache}
            disabled={!cacheStats || cacheStats.fileCount === 0}
            className="text-red-400 border-red-400/30 hover:bg-red-400/10"
          >
            <Trash2 className="h-4 w-4 mr-2" />
            {getLocaleString('clearAll', currentLanguage) || '–û—á–∏—Å—Ç–∏—Ç—å –≤—Å–µ'}
          </Button>
        </div>
      </div>

      {/* –°–ø–∏—Å–æ–∫ –∫–µ—à–∏—Ä–æ–≤–∞–Ω–Ω—ã—Ö —Ñ–∞–π–ª–æ–≤ */}
      {cacheStats && cacheStats.files.length > 0 && (
        <div className="border-t border-slate-700 pt-4">
          <div className="text-sm font-medium text-white mb-3">
            {getLocaleString('cachedFiles', currentLanguage) || '–ö–µ—à–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ —Ñ–∞–π–ª—ã'}
          </div>
          <div className="space-y-2 max-h-40 overflow-y-auto">
            {cacheStats.files.map((file, index) => (
              <div key={index} className="flex items-center justify-between text-xs p-2 bg-slate-700/50 rounded">
                <div className="flex-1 min-w-0">
                  <div className="text-white truncate">
                    {file.episodeSlug}
                  </div>
                  <div className="text-slate-400">
                    {formatBytes(file.size)}
                  </div>
                </div>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => audioCacheService.removeAudioFromCache(file.url)}
                  className="h-6 w-6 p-0 text-red-400 hover:text-red-300"
                >
                  <Trash2 className="h-3 w-3" />
                </Button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default AudioCacheManager;

import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { supabase } from '@/lib/supabaseClient';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogFooter } from '@/components/ui/dialog';
import { Progress } from '@/components/ui/progress'; 
import { UploadCloud, FileAudio, X, Loader2, CheckCircle } from 'lucide-react';
import { useToast } from '@/components/ui/use-toast';
import { getLocaleString } from '@/lib/locales';
import r2Service from '@/lib/r2Service'; 
import assemblyAIService from '@/lib/assemblyAIService';
import { getFileNameWithoutExtension } from '@/lib/utils';

const AudioUploader = ({ isOpen, onClose, onUploadSuccess, currentLanguage }) => {
  const [files, setFiles] = useState([]);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadProgressDetails, setUploadProgressDetails] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadError, setUploadError] = useState(null);
  const [uploadComplete, setUploadComplete] = useState(false);
  const { toast } = useToast();

  const onDrop = useCallback(acceptedFiles => {
    setFiles(prevFiles => [...prevFiles, ...acceptedFiles.map(file => Object.assign(file, {
      preview: URL.createObjectURL(file)
    }))]);
    setUploadError(null);
    setUploadComplete(false);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: { 'audio/*': ['.mp3', '.wav', '.m4a', '.aac', '.ogg', '.flac'] },
    multiple: true
  });

  const removeFile = (fileToRemove) => {
    setFiles(files.filter(file => file !== fileToRemove));
  };

  const generateEpisodeSlug = (dateStr, lang) => {
    if (!dateStr) dateStr = new Date().toISOString().split('T')[0]; // Fallback to current date
    return `${dateStr}_${lang}`;
  };

  const extractDetailsFromFilename = (filename) => {
    const nameWithoutExt = getFileNameWithoutExtension(filename);
    let title = nameWithoutExt;
    let lang = 'all';
    let parsedDate = null; // YYYY-MM-DD

    const dateMatch = nameWithoutExt.match(/(\d{4})\.(\d{2})\.(\d{2})/);
    if (dateMatch) {
      parsedDate = `${dateMatch[1]}-${dateMatch[2]}-${dateMatch[3]}`;
      title = title.replace(dateMatch[0], '').trim().replace(/^[-_]+|[-_]+$/g, '');
    } else {
      // Try to parse if filename itself is a date like YYYY-MM-DD
      const strictDateMatch = nameWithoutExt.match(/^(\d{4})-(\d{2})-(\d{2})$/);
      if (strictDateMatch) {
        parsedDate = `${strictDateMatch[1]}-${strictDateMatch[2]}-${strictDateMatch[3]}`;
        title = ''; // No specific title part if filename is just date
      }
    }

    const langSuffixMatch = title.match(/_([RUruESes]{2})$/);
    if (langSuffixMatch) {
        lang = langSuffixMatch[1].toLowerCase();
        title = title.substring(0, title.lastIndexOf(langSuffixMatch[0])).trim().replace(/^[-_]+|[-_]+$/g, '');
    }

    title = title || (parsedDate ? `Meditaci√≥n ${parsedDate}` : nameWithoutExt);

    return { title, lang, parsedDate };
  };


  const handleUpload = async () => {
    if (files.length === 0) return;

    setIsUploading(true);
    setUploadProgress(0);
    setUploadProgressDetails(null);
    setUploadError(null);
    setUploadComplete(false);

    for (const file of files) {
      try {
        const { fileUrl: workerFileUrl, fileKey, bucketName } = await r2Service.uploadFile(
          file, 
          (progress, details) => {
            setUploadProgress(progress);
            setUploadProgressDetails(details);
            if (details?.message) {
              console.log(`[Upload UI] ${file.name}: ${progress}% ‚Äî ${details.message} (${details?.uploadedMB || '0.00'} / ${details?.totalMB || '0.00'} MB)`);
            }
          },
          currentLanguage
        );
        toast({ title: getLocaleString('uploadToR2Success', currentLanguage), description: `${file.name} (Archive.org: ${bucketName})` });

        const audioForDuration = new Audio(URL.createObjectURL(file));
        let duration = 0;
        try {
            duration = await new Promise((resolve) => {
                audioForDuration.onloadedmetadata = () => resolve(audioForDuration.duration);
                audioForDuration.onerror = () => resolve(0); 
            });
        } catch (e) { console.error("Error getting duration",e); duration = 0; }

        const { title: episodeTitle, lang: fileLang, parsedDate: episodeDate } = extractDetailsFromFilename(file.name);
        const episodeSlug = generateEpisodeSlug(episodeDate || new Date().toISOString().split('T')[0], fileLang);

        const { data: episodeData, error: dbError } = await supabase
          .from('episodes')
          .insert([
            { 
              slug: episodeSlug,
              title: episodeTitle, 
              lang: fileLang,
              date: episodeDate || new Date().toISOString().split('T')[0],
              audio_url: workerFileUrl, 
              r2_object_key: fileKey,
              r2_bucket_name: bucketName,
              duration: Math.round(duration || 0),
            }
          ])
          .select('slug')
          .maybeSingle();

        if (dbError) {
            if (dbError.code === '23505') { // Unique violation for slug
                toast({ title: getLocaleString('errorGeneric', currentLanguage), description: `–≠–ø–∏–∑–æ–¥ —Å slug ${episodeSlug} —É–∂–µ —Å—É—â–µ—Å—Ç–≤—É–µ—Ç.`, variant: 'destructive'});
            } else {
                throw new Error(`Supabase error: ${dbError.message}`);
            }
            setIsUploading(false);
            return;
        }
        toast({ title: getLocaleString('metadataSavedSuccess', currentLanguage), description: file.name });

        if (episodeData && episodeData.slug) {
          let assemblyLangCode;
          let transcriptLangForDb;

          if (fileLang === 'all') {
            assemblyLangCode = currentLanguage === 'ru' ? 'ru' : 'es'; 
            transcriptLangForDb = currentLanguage;
          } else {
            assemblyLangCode = fileLang;
            transcriptLangForDb = fileLang;
          }

          const transcriptJob = await assemblyAIService.submitTranscription(
            workerFileUrl, 
            assemblyLangCode, 
            episodeData.slug, // Pass episode slug to webhook
            currentLanguage 
          );

          const { error: transcriptDbError } = await supabase
            .from('transcripts')
            .insert([{
              episode_slug: episodeData.slug,
              lang: transcriptLangForDb, 
              assemblyai_transcript_id: transcriptJob.id,
              status: transcriptJob.status,
            }]);

          if (transcriptDbError) console.error("Error saving initial transcript job to DB:", transcriptDbError);

          toast({ title: getLocaleString('transcriptionStarted', currentLanguage), description: `${file.name} (${assemblyLangCode})` });
        }

      } catch (error) {
        console.error('Upload process error for file', file.name, ':', error);
        setUploadError(`${getLocaleString('errorUploadingFile', currentLanguage, {fileName: file.name})}: ${error.message}`);
        toast({ title: getLocaleString('uploadError', currentLanguage), description: error.message, variant: 'destructive'});
        setIsUploading(false); 
        return; 
      }
    }

    setUploadComplete(true);
    setIsUploading(false);
    setFiles([]); 
    if (onUploadSuccess) onUploadSuccess();
  };

  const handleClose = () => {
    if (isUploading) return; 
    setFiles([]);
    setUploadProgress(0);
    setUploadProgressDetails(null);
    setUploadError(null);
    setUploadComplete(false);
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-lg bg-slate-800 border-slate-700 text-white">
        <DialogHeader>
          <DialogTitle className="text-purple-300">{getLocaleString('uploadAudioFiles', currentLanguage)}</DialogTitle>
          <DialogDescription className="text-slate-400">
            {getLocaleString('uploadAudioDescription', currentLanguage)}
          </DialogDescription>
        </DialogHeader>

        <div {...getRootProps()} className={`mt-4 p-8 border-2 border-dashed rounded-lg text-center cursor-pointer
          ${isDragActive ? 'border-purple-500 bg-purple-500/10' : 'border-slate-600 hover:border-slate-500'}`}>
          <input {...getInputProps()} />
          <UploadCloud className="mx-auto h-12 w-12 text-slate-500 mb-2" />
          {isDragActive ? (
            <p className="text-purple-400">{getLocaleString('dropFilesHere', currentLanguage)}</p>
          ) : (
            <p className="text-slate-400">{getLocaleString('dragOrClickUpload', currentLanguage)}</p>
          )}
          <p className="text-xs text-slate-500 mt-1">{getLocaleString('supportedFormats', currentLanguage)}: MP3, WAV, M4A, etc.</p>
        </div>

        {files.length > 0 && (
          <div className="mt-4 max-h-40 overflow-y-auto space-y-2 pr-2">
            {files.map((file, index) => (
              <div key={index} className="flex items-center justify-between p-2 bg-slate-700 rounded">
                <div className="flex items-center gap-2">
                  <FileAudio className="h-5 w-5 text-purple-400" />
                  <span className="text-sm truncate" title={file.name}>{file.name}</span>
                </div>
                {!isUploading && (
                  <Button variant="ghost" size="icon_sm" onClick={() => removeFile(file)} className="text-red-400 hover:text-red-300">
                    <X className="h-4 w-4" />
                  </Button>
                )}
              </div>
            ))}
          </div>
        )}

        {isUploading && (
          <div className="mt-4">
            <Progress value={uploadProgress} className="w-full [&>div]:bg-purple-500" />
            <div className="text-sm text-center mt-2 text-purple-300">
              {uploadProgressDetails?.message || getLocaleString('uploading', currentLanguage)} {uploadProgress}%
              {uploadProgressDetails && (
                <div className="text-xs text-purple-200 mt-1">
                  {uploadProgressDetails.uploadedMB} / {uploadProgressDetails.totalMB} MB
                </div>
              )}
            </div>
          </div>
        )}

        {uploadError && (
          <p className="mt-4 text-sm text-red-400 text-center">{uploadError}</p>
        )}

        {uploadComplete && !uploadError && (
           <div className="mt-4 text-center text-green-400 flex items-center justify-center">
             <CheckCircle className="h-5 w-5 mr-2"/>
             <p>{getLocaleString('allFilesUploadedSuccessfully', currentLanguage)}</p>
           </div>
        )}

        <DialogFooter className="mt-6">
          <Button variant="outline" onClick={handleClose} disabled={isUploading} className="border-slate-600 hover:bg-slate-700">
            {getLocaleString('close', currentLanguage)}
          </Button>
          <Button onClick={handleUpload} disabled={isUploading || files.length === 0} className="bg-purple-600 hover:bg-purple-700">
            {isUploading ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                {getLocaleString('uploadingInProgress', currentLanguage)}
              </>
            ) : (
              getLocaleString('startUpload', currentLanguage)
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
};

