import React from 'react';
import { Button } from '@/components/ui/button';
import { Play, Pause, RotateCw, RotateCcw, X } from 'lucide-react';
import { getLocaleString } from '@/lib/locales';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';

const FloatingPlayerControlsReadingMode = ({ 
  episodeTitle, 
  isPlaying, 
  onPlayPause, 
  onSkipSeconds,
  onClose,
  currentLanguage,
  audioRef 
}) => {
  if (!episodeTitle) return null;

  const handlePlayPause = () => {
    if (onPlayPause) {
      onPlayPause();
    } else if (audioRef && audioRef.current) { 
      if (audioRef.current.paused) {
        audioRef.current.play().catch(e => console.error("Reading mode play error (direct):", e));
      } else {
        audioRef.current.pause();
      }
    }
  };

  const handleSkip = (seconds) => {
     if (onSkipSeconds) {
        onSkipSeconds(seconds);
     } else if (audioRef && audioRef.current) {
        const newTime = Math.max(0, Math.min(audioRef.current.duration || 0, audioRef.current.currentTime + seconds));
        audioRef.current.currentTime = newTime;
    }
  };

  return (
    <div
      className="fixed top-0 left-0 right-0 z-[110] bg-slate-50/90 backdrop-blur-md shadow-lg border-b border-slate-300"
    >
      <div className="container mx-auto px-3 py-2 flex items-center justify-between max-w-3xl">
        <div className="flex-grow min-w-0 mx-2 text-center">
          <p className="text-sm font-semibold text-slate-700 truncate" title={episodeTitle}>
            {episodeTitle}
          </p>
          <p className="text-xs text-slate-500 truncate">
            {isPlaying ? getLocaleString('nowPlaying', currentLanguage) : getLocaleString('paused', currentLanguage)}
          </p>
        </div>

        <div className="flex items-center gap-1 shrink-0">
          <TooltipProvider>
            <Tooltip>
              <TooltipTrigger asChild>
                <Button 
                  variant="ghost" 
                  size="icon" 
                  onClick={() => handleSkip(-10)} 
                  className="text-slate-700 hover:text-purple-700 hover:bg-purple-100 h-9 w-9"
                  aria-label={getLocaleString('skipBackward10', currentLanguage) || "Skip backward 10s"}
                >
                  <RotateCcw className="h-5 w-5" />
                </Button>
              </TooltipTrigger>
              <TooltipContent side="bottom" className="bg-slate-800 text-white border-slate-700">
                  <p>{getLocaleString('skipBackward10Tooltip', currentLanguage)}</p>
              </TooltipContent>
            </Tooltip>
            <Tooltip>
              <TooltipTrigger asChild>
                <Button 
                  variant="ghost" 
                  size="icon" 
                  onClick={handlePlayPause} 
                  className="text-slate-800 hover:text-purple-700 bg-purple-500/20 hover:bg-purple-500/30 rounded-full h-10 w-10"
                  aria-label={isPlaying ? getLocaleString('pause', currentLanguage) : getLocaleString('play', currentLanguage)}
                >
                  {isPlaying ? <Pause className="h-5 w-5" /> : <Play className="h-5 w-5" />}
                </Button>
              </TooltipTrigger>
              <TooltipContent side="bottom" className="bg-slate-800 text-white border-slate-700">
                  <p>{isPlaying ? getLocaleString('pauseTooltip', currentLanguage) : getLocaleString('playTooltip', currentLanguage)}</p>
              </TooltipContent>
            </Tooltip>
            <Tooltip>
              <TooltipTrigger asChild>
                <Button 
                  variant="ghost" 
                  size="icon" 
                  onClick={() => handleSkip(10)} 
                  className="text-slate-700 hover:text-purple-700 hover:bg-purple-100 h-9 w-9"
                  aria-label={getLocaleString('skipForward10', currentLanguage) || "Skip forward 10s"}
                >
                  <RotateCw className="h-5 w-5" />
                </Button>
              </TooltipTrigger>
              <TooltipContent side="bottom" className="bg-slate-800 text-white border-slate-700">
                  <p>{getLocaleString('skipForward10Tooltip', currentLanguage)}</p>
              </TooltipContent>
            </Tooltip>
            <Tooltip>
              <TooltipTrigger asChild>
                <Button 
                  variant="ghost" 
                  size="icon" 
                  onClick={onClose}
                  className="text-slate-700 hover:text-red-600 hover:bg-red-100 h-9 w-9 ml-2"
                  aria-label={getLocaleString('exitReadingMode', currentLanguage)}
                >
                  <X className="h-5 w-5" />
                </Button>
              </TooltipTrigger>
              <TooltipContent side="bottom" className="bg-slate-800 text-white border-slate-700">
                  <p>{getLocaleString('exitReadingModeTooltip', currentLanguage)}</p>
              </TooltipContent>
            </Tooltip>
          </TooltipProvider>
        </div>
      </div>
    </div>
  );
};

export default FloatingPlayerControlsReadingMode;

import React from 'react';
import { Play, Pause, RotateCcw, RotateCw } from 'lucide-react';
import { Button } from '@/components/ui/button';

const PlayerControls = ({ 
  isPlaying, 
  onPlayPause, 
  onSkip, 
  variant = "default",
  onAdjustTime,
  seekAudio,
  currentTime, 
  mainPlayerIsPlaying, 
  mainPlayerTogglePlayPause, 
  mainPlayerSeekAudio,
  audioRefCurrentTime, 
  playerDuration 
 }) => {
  const controlSize = variant === "compact" ? "h-10 w-10 md:h-11 md:w-11" : "h-11 w-11 md:h-13 md:w-13";
  const iconSize = variant === "compact" ? "h-5 w-5 md:h-5 md:w-5" : "h-5 w-5 md:h-6 md:w-6";
  const mainButtonSize = variant === "compact" ? "h-14 w-14 md:h-16 md:w-16" : "h-16 w-16 md:h-18 md:w-18";
  const mainIconSize = variant === "compact" ? "h-7 w-7 md:h-8 md:w-8" : "h-8 w-8 md:h-9 md:w-9";

  if (variant === "timeAdjustment") {
    const handleTimeAdjustAndPlay = (amount) => {
      const currentAudioTime = audioRefCurrentTime ?? 0;
      const duration = playerDuration ?? Infinity;
      const newTime = Math.max(0, Math.min(duration, currentAudioTime + amount));

      if (onAdjustTime) { 
         onAdjustTime(amount); 
      } else if (mainPlayerSeekAudio) { 
         mainPlayerSeekAudio(newTime, true); 
      } else if (seekAudio) {
         seekAudio(newTime, true);
      }
    };

    return (
       <div className="flex items-center justify-center gap-1">
        <Button variant="outline" size="sm" className="px-2 py-1 h-auto bg-white/10 hover:bg-white/20 border-white/20" onClick={() => handleTimeAdjustAndPlay(-10)}>
          <RotateCcw className="h-3.5 w-3.5 mr-1" /> -10s
        </Button>
        <Button variant="outline" size="sm" className="px-2 py-1 h-auto bg-white/10 hover:bg-white/20 border-white/20" onClick={() => handleTimeAdjustAndPlay(-1)}>
          <RotateCcw className="h-3.5 w-3.5 mr-1" /> -1s
        </Button>
        <Button variant="outline" size="icon" className="h-8 w-8 bg-white/10 hover:bg-white/20 border-white/20" 
          onClick={() => {
             if (mainPlayerTogglePlayPause) mainPlayerTogglePlayPause(); 
             else if (onPlayPause) onPlayPause(); 
          }}>
          {(mainPlayerIsPlaying !== undefined ? mainPlayerIsPlaying : isPlaying) ? <Pause className="h-4 w-4" /> : <Play className="h-4 w-4" />}
        </Button>
        <Button variant="outline" size="sm" className="px-2 py-1 h-auto bg-white/10 hover:bg-white/20 border-white/20" onClick={() => handleTimeAdjustAndPlay(1)}>
          +1s <RotateCw className="h-3.5 w-3.5 ml-1" />
        </Button>
        <Button variant="outline" size="sm" className="px-2 py-1 h-auto bg-white/10 hover:bg-white/20 border-white/20" onClick={() => handleTimeAdjustAndPlay(10)}>
          +10s <RotateCw className="h-3.5 w-3.5 ml-1" />
        </Button>
      </div>
    );
  }

  return (
    <div className={`flex items-center justify-center gap-2 md:gap-2.5 ${variant === "compact" ? "my-0" : ""}`}>
      <Button 
        variant="ghost" 
        size="icon"
        onClick={() => {

          onSkip(-10);
        }}
        className={`text-white hover:text-white/80 hover:bg-white/15 ${controlSize}`}
