const SpeakerAssignmentDialog = ({
  isOpen,
  onClose,
  segment,
  onSave,
  currentLanguage
}) => {
  const [speakerName, setSpeakerName] = useState('');

  const quickAssignNames = useMemo(() => {
    return currentLanguage === 'ru' ? { pepe: '–ü–µ–ø–µ', maria: '–ú–∞—Ä–∏—è'} : { pepe: 'Pepe', maria: 'Maria'};
  }, [currentLanguage]);

  useEffect(() => {
    if (isOpen && segment) {
      const currentSpeaker = segment.speaker;
