const MAX_SEGMENT_DURATION_MS = 120000; 
const PREFERRED_SPLIT_WINDOW_MS = 30000; 

const findLastSentenceEndIndex = (textBlock, maxChars) => {
  if (!textBlock || textBlock.length <= maxChars) return textBlock.length;

  let searchEnd = maxChars;
  let searchStart = Math.max(0, maxChars - (PREFERRED_SPLIT_WINDOW_MS / 1000 * 15)); 

  for (let i = searchEnd; i >= searchStart; i--) {
    if (textBlock[i] === '.' || textBlock[i] === '?' || textBlock[i] === '!') {
      if (i + 1 < textBlock.length && textBlock[i+1] === ' ') {
        return i + 2; 
      }
      return i + 1; 
    }
  }

  let lastSpace = -1;
  for (let i = searchEnd; i >= searchStart; i--) {
    if (textBlock[i] === ' ') {
      lastSpace = i;
      break;
    }
  }
  return lastSpace !== -1 ? lastSpace + 1 : maxChars;
};


export const splitLongUtterance = (utterance) => {
  const utteranceDuration = utterance.end - utterance.start;
  if (utteranceDuration <= MAX_SEGMENT_DURATION_MS) {
    return [utterance];
  }

  const newUtterances = [];
  let currentStart = utterance.start;

  if (utterance.words && utterance.words.length > 0) {
    let currentWords = [];
    let currentText = "";

    for (let i = 0; i < utterance.words.length; i++) {
      const word = utterance.words[i];
      const potentialChunkEnd = word.end;

      if (potentialChunkEnd - currentStart > MAX_SEGMENT_DURATION_MS && currentWords.length > 0) {
        let splitAtWordIndex = currentWords.length -1; 
        let searchBackwardsFromIndex = currentWords.length -1;

        for (let j = searchBackwardsFromIndex; j >=0; j--) {
            const prevWord = currentWords[j];
            if (potentialChunkEnd - prevWord.start > PREFERRED_SPLIT_WINDOW_MS && j < searchBackwardsFromIndex) break; 
            if (/[.?!]$/.test(prevWord.text)) {
                splitAtWordIndex = j;
                break;
            }
        }

        const wordsForThisChunk = currentWords.slice(0, splitAtWordIndex + 1);
        const textForThisChunk = wordsForThisChunk.map(w => w.text).join(" ");
        const endForThisChunk = wordsForThisChunk[wordsForThisChunk.length -1].end;

        newUtterances.push({
          ...utterance,
          start: currentStart,
          end: endForThisChunk,
          text: textForThisChunk,
          words: [...wordsForThisChunk],
          id: `${utterance.id || utterance.start}-split-${newUtterances.length}-${Date.now()}`
        });

        currentWords = currentWords.slice(splitAtWordIndex + 1);
        currentText = currentWords.map(w => w.text).join(" ");
        currentStart = currentWords.length > 0 ? currentWords[0].start : potentialChunkEnd; 

        if(currentWords.length === 0) { 
           if (i < utterance.words.length){ 
             currentWords.push(word);
             currentText = word.text;
             currentStart = word.start;
           } else { 
             continue;
           }
        } else { 
          currentWords.push(word);
          currentText = currentWords.map(w => w.text).join(" ");
        }

      } else {
        currentWords.push(word);
        currentText = currentText ? `${currentText} ${word.text}` : word.text;
      }
    }

    if (currentWords.length > 0) {
      newUtterances.push({
        ...utterance,
        start: currentStart,
        end: utterance.end, 
        text: currentText.trim(),
        words: currentWords,
        id: `${utterance.id || utterance.start}-split-${newUtterances.length}-${Date.now()}`
      });
    }
  } else { 
    const numChunks = Math.ceil(utteranceDuration / MAX_SEGMENT_DURATION_MS);
    let textOffset = 0;

    for (let i = 0; i < numChunks; i++) {
      const chunkStartMs = utterance.start + i * MAX_SEGMENT_DURATION_MS;
      const chunkDurationMs = Math.min(MAX_SEGMENT_DURATION_MS, utterance.end - chunkStartMs);
      const chunkEndMs = chunkStartMs + chunkDurationMs;

      const proportionOfDuration = chunkDurationMs / utteranceDuration;
      let estimatedCharsForChunk = Math.floor(utterance.text.length * proportionOfDuration);

      let actualCharsForChunk;
      if (i === numChunks - 1) {
        actualCharsForChunk = utterance.text.length - textOffset;
      } else {
        actualCharsForChunk = findLastSentenceEndIndex(utterance.text.substring(textOffset), estimatedCharsForChunk);
         if (textOffset + actualCharsForChunk > utterance.text.length) { 
            actualCharsForChunk = utterance.text.length - textOffset;
        }
      }

      const chunkText = utterance.text.substring(textOffset, textOffset + actualCharsForChunk);
      textOffset += actualCharsForChunk;

      if (chunkText.trim()) {
        newUtterances.push({
          ...utterance,
          start: chunkStartMs,
          end: chunkEndMs,
          text: chunkText.trim(),
          words: [], 
          id: `${utterance.id || utterance.start}-textsplit-${i}-${Date.now()}`
        });
      }
    }
  }

  return newUtterances.length > 0 ? newUtterances : [utterance];
};


export const splitTranscriptToSegments = (utterances, maxSegmentDurationMs = 120000) => {
  if (!Array.isArray(utterances) || utterances.length === 0) return [];
  const segments = [];
  let currentSegment = [];
  let segmentStart = null;
  let segmentEnd = null;

  for (let i = 0; i < utterances.length; i++) {
    const utt = utterances[i];
    if (currentSegment.length === 0) {
      segmentStart = utt.start;
      segmentEnd = utt.end;
      currentSegment.push(utt);
      continue;
    }
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –Ω–µ –ø—Ä–µ–≤—ã—Å–∏—Ç –ª–∏ –¥–æ–±–∞–≤–ª–µ–Ω–∏–µ —ç—Ç–æ–≥–æ utterance –ª–∏–º–∏—Ç
    if ((utt.end - segmentStart) > maxSegmentDurationMs) {
      // –ï—Å–ª–∏ —Ç–µ–∫—É—â–µ–µ utterance —Å–∞–º–æ –ø–æ —Å–µ–±–µ –¥–ª–∏–Ω–Ω–æ–µ ‚Äî —Ä–∞–∑–±–∏—Ç—å –µ–≥–æ
      if ((utt.end - utt.start) > maxSegmentDurationMs) {
        // –ò—Å–ø–æ–ª—å–∑—É–µ–º splitLongUtterance –¥–ª—è —ç—Ç–æ–≥–æ utterance
        const splitted = splitLongUtterance(utt);
        // –ü–µ—Ä–≤—ã–π –∫—É—Å–æ–∫ ‚Äî –∑–∞–≤–µ—Ä—à–∞–µ—Ç —Ç–µ–∫—É—â–∏–π —Å–µ–≥–º–µ–Ω—Ç, –æ—Å—Ç–∞–ª—å–Ω—ã–µ ‚Äî –Ω–æ–≤—ã–µ —Å–µ–≥–º–µ–Ω—Ç—ã
        if (splitted.length > 0) {
          // –ü–µ—Ä–≤—ã–π –∫—É—Å–æ–∫ ‚Äî –∑–∞–≤–µ—Ä—à–∞–µ—Ç —Ç–µ–∫—É—â–∏–π —Å–µ–≥–º–µ–Ω—Ç
          segments.push([...currentSegment, splitted[0]]);
          // –û—Å—Ç–∞–ª—å–Ω—ã–µ –∫—É—Å–∫–∏ ‚Äî –æ—Ç–¥–µ–ª—å–Ω—ã–µ —Å–µ–≥–º–µ–Ω—Ç—ã
          for (let j = 1; j < splitted.length; j++) {
            segments.push([splitted[j]]);
          }
        } else {
          segments.push([...currentSegment, utt]);
        }
        currentSegment = [];
        segmentStart = null;
        segmentEnd = null;
      } else {
        // –ó–∞–≤–µ—Ä—à–∞–µ–º —Ç–µ–∫—É—â–∏–π —Å–µ–≥–º–µ–Ω—Ç
        segments.push([...currentSegment]);
        // –ù–∞—á–∏–Ω–∞–µ–º –Ω–æ–≤—ã–π —Å–µ–≥–º–µ–Ω—Ç —Å —Ç–µ–∫—É—â–µ–≥–æ utterance
        currentSegment = [utt];
        segmentStart = utt.start;
        segmentEnd = utt.end;
      }
    } else {
      // –î–æ–±–∞–≤–ª—è–µ–º utterance –≤ —Ç–µ–∫—É—â–∏–π —Å–µ–≥–º–µ–Ω—Ç
      currentSegment.push(utt);
      segmentEnd = utt.end;
    }
  }
  if (currentSegment.length > 0) {
    segments.push([...currentSegment]);
  }
  return segments;
};


export const processTranscriptData = (data) => {
  if (!data || !Array.isArray(data.utterances)) {
    return { ...data, utterances: [] };
  }

  // Debug: basic stats before processing
  try {
    console.log('[TranscriptProcessing] Incoming utterances:', data.utterances.length);
  } catch (e) {}

  const processedUtterances = data.utterances.flatMap(utt => {
    const utteranceWithWords = {
      ...utt,
      words: data.words?.filter(w => w.start >= utt.start && w.end <= utt.end && w.confidence > 0) || utt.words || []
    };
    const splitted = splitLongUtterance(utteranceWithWords);
    try {
      if ((utt.end - utt.start) > 120000) {
        console.log('[TranscriptProcessing] Long utterance detected:', {
          start: utt.start,
          end: utt.end,
          durationMs: utt.end - utt.start,
          originalTextLen: (utt.text || '').length,
          splitCount: splitted.length
        });
      }
    } catch (e) {}
    return splitted;
  }).filter(utt => utt.text && utt.text.trim() !== "");

  try {
    console.log('[TranscriptProcessing] Processed utterances:', processedUtterances.length);
  } catch (e) {}

  return { ...data, utterances: processedUtterances, words: data.words };
};

import { useState, useEffect, useCallback, useRef } from 'react';
import { supabase } from '@/lib/supabaseClient';
import assemblyAIService from '@/lib/assemblyAIService.js';
import { getLocaleString } from '@/lib/locales';
import { processTranscriptData } from '@/hooks/transcript/transcriptProcessingUtils';


const useTranscriptAssemblyAI = (
  episodeSlug, 
  episodeAudioUrl, 
  episodeLang, 
  currentLanguage, 
  toast, 
  setTranscriptState, 
  setTranscriptDbIdState, 
  setIsLoadingTranscriptState, 
  setTranscriptErrorState,
  currentTranscriptState,
  currentTranscriptDbId
) => {
  const [isPollingTranscript, setIsPollingTranscript] = useState(false);
  const [transcriptionJobId, setTranscriptionJobId] = useState(null);
  const pollingTimeoutRef = useRef(null);

  const determineAssemblyLangForEpisode = useCallback(() => {
    if (episodeLang === 'ru') return 'ru';
    if (episodeLang === 'es') return 'es';
    return currentLanguage === 'ru' ? 'ru' : 'es';
  }, [episodeLang, currentLanguage]);

  const pollTranscriptStatus = useCallback(async (assemblyId, dbTranscriptId, assemblyLang) => {
    if (isPollingTranscript) return;
    setIsPollingTranscript(true);
    try {
      const result = await assemblyAIService.getTranscriptionResult(assemblyId, currentLanguage);
      if (result.status === 'completed') {
        setTranscriptState(result); 
        toast({ title: getLocaleString('transcriptionCompletedTitle', currentLanguage), description: getLocaleString('transcriptionCompletedDescription', currentLanguage) });

        const { error: updateError } = await supabase
          .from('transcripts')
          .update({ status: 'completed', transcript_data: result, edited_transcript_data: processTranscriptData(result) })
          .eq('id', dbTranscriptId);
        if (updateError) {
            console.error("Error updating transcript in DB after polling:", updateError);
        }
      } else if (result.status === 'error') {
        const errorMessage = result.error || getLocaleString('unknownAssemblyError', currentLanguage);
        setTranscriptErrorState(getLocaleString('transcriptionErrorAssembly', currentLanguage) + `: ${errorMessage}`);
        toast({ title: getLocaleString('transcriptionErrorTitle', currentLanguage), description: getLocaleString('transcriptionErrorAssembly', currentLanguage) + `: ${errorMessage}`, variant: 'destructive' });
        await supabase.from('transcripts').update({ status: 'error', transcript_data: result }).eq('id', dbTranscriptId);
      } else {
        pollingTimeoutRef.current = setTimeout(() => pollTranscriptStatus(assemblyId, dbTranscriptId, assemblyLang), 15000);
      }
    } catch (err) {
      toast({ title: getLocaleString('errorGeneric', currentLanguage), description: getLocaleString('errorPollingTranscript', currentLanguage), variant: 'destructive' });
    } finally {
      setIsPollingTranscript(false);
    }
  }, [isPollingTranscript, toast, currentLanguage, setTranscriptState, setTranscriptErrorState]);

  const handleStartTranscription = useCallback(async (langCodeForAssembly = null, audioUrlForTranscription = null, existingDbTranscriptEntry = null) => {
    if (!episodeSlug || (!episodeAudioUrl && !audioUrlForTranscription)) {
      toast({ title: getLocaleString('errorGeneric', currentLanguage), description: getLocaleString('missingEpisodeOrUrl', currentLanguage), variant: 'destructive' });
      return;
    }

    if (transcriptionJobId && (existingDbTranscriptEntry?.status === 'processing' || existingDbTranscriptEntry?.status === 'queued')) {
        console.log("Transcription request already sent and in progress. Skipping new request.");
        return;
    }

    const finalAudioUrl = audioUrlForTranscription || episodeAudioUrl;
    setIsLoadingTranscriptState(true);
    setTranscriptErrorState(null);

    const assemblyLangToUse = langCodeForAssembly || determineAssemblyLangForEpisode();
    const transcriptLangForDb = episodeLang === 'all' ? currentLanguage : episodeLang;

    try {
      const job = await assemblyAIService.submitTranscription(finalAudioUrl, assemblyLangToUse, episodeSlug, currentLanguage, transcriptLangForDb);
      setTranscriptionJobId(job.id);

      let dbOp = existingDbTranscriptEntry?.id
        ? supabase.from('transcripts').update({ assemblyai_transcript_id: job.id, status: job.status, lang: transcriptLangForDb, updated_at: new Date().toISOString(), edited_transcript_data: null }).eq('id', existingDbTranscriptEntry.id)
        : supabase.from('transcripts').insert([{ episode_slug: episodeSlug, lang: transcriptLangForDb, assemblyai_transcript_id: job.id, status: job.status, edited_transcript_data: null }]);

      const { error: dbError, data: transcriptEntries } = await dbOp.select('id, edited_transcript_data').single();
      if (dbError) throw dbError;
      if (!transcriptEntries) throw new Error("Failed to get transcript entry ID from DB operation.");
      const newDbTranscriptId = transcriptEntries.id;
      setTranscriptDbIdState(newDbTranscriptId);

      if (job.status === 'queued' || job.status === 'processing') {
        toast({ title: getLocaleString('transcriptionSubmittedTitle', currentLanguage), description: getLocaleString('transcriptionSubmittedDescription', currentLanguage, { lang: assemblyLangToUse }) });
        if (pollingTimeoutRef.current) clearTimeout(pollingTimeoutRef.current);
        pollTranscriptStatus(job.id, newDbTranscriptId, assemblyLangToUse);
      } else if (job.status === 'error') {
        const errorMessage = job.error || getLocaleString('unknownAssemblyError', currentLanguage);
        setTranscriptErrorState(getLocaleString('transcriptionErrorAssembly', currentLanguage) + `: ${errorMessage}`);
        await supabase.from('transcripts').update({ status: 'error', transcript_data: job }).eq('id', newDbTranscriptId);
      }
    } catch (err) {
      setTranscriptErrorState(getLocaleString('errorStartingTranscription', currentLanguage, { errorMessage: err.message }));
      toast({ title: getLocaleString('errorGeneric', currentLanguage), description: err.message, variant: 'destructive' });
    } finally {
      setIsLoadingTranscriptState(false);
    }
  }, [
      episodeSlug, 
      episodeAudioUrl, 
      toast, 
      currentLanguage, 
      determineAssemblyLangForEpisode, 
      pollTranscriptStatus, 
      episodeLang, 
      transcriptionJobId,
      setIsLoadingTranscriptState,
      setTranscriptErrorState,
      setTranscriptDbIdState
    ]);

  useEffect(() => {
    return () => {
      if (pollingTimeoutRef.current) {
        clearTimeout(pollingTimeoutRef.current);
      }
    };
  }, []);

  return {
    isPollingTranscript,
    setIsPollingTranscript,
    transcriptionJobId,
    setTranscriptionJobId,
    pollingTimeoutRef,
    determineAssemblyLangForEpisode,
    pollTranscriptStatus,
    handleStartTranscription,
  };
};

