        throw new Error("–ü–µ—Ä–µ–≤–æ–¥ –≤–µ—Ä–Ω—É–ª –ø—É—Å—Ç–æ–π —Ç–µ–∫—Å—Ç.");
      }
    } catch (error) {
      console.error("‚ùå Error translating timings text:", error);
      console.error("‚ùå Error details:", {
        name: error.name,
        message: error.message,
        stack: error.stack?.substring(0, 300)
      });

      // Determine error type and provide specific feedback
      let errorTitle = getLocaleString('translateTimingsErrorTitle', currentLanguage);
      let errorDescription = error.message;

      if (error.message.includes('API key')) {
        errorTitle = "üîë –û—à–∏–±–∫–∞ API –ö–ª—é—á–∞";
        errorDescription = "OpenAI API –∫–ª—é—á –Ω–µ–¥–æ—Å—Ç—É–ø–µ–Ω –∏–ª–∏ –Ω–µ–¥–µ–π—Å—Ç–≤–∏—Ç–µ–ª–µ–Ω. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ —Å–µ—Ä–≤–µ—Ä–∞.";
      } else if (error.message.includes('quota') || error.message.includes('limit')) {
        errorTitle = "üí≥ –ü—Ä–µ–≤—ã—à–µ–Ω –õ–∏–º–∏—Ç";
        errorDescription = "–ü—Ä–µ–≤—ã—à–µ–Ω –ª–∏–º–∏—Ç –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è OpenAI API. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –ø–æ–∑–∂–µ.";
      } else if (error.message.includes('network') || error.message.includes('fetch')) {
        errorTitle = "üåê –°–µ—Ç–µ–≤–∞—è –û—à–∏–±–∫–∞";
        errorDescription = "–ü—Ä–æ–±–ª–µ–º–∞ —Å –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ–º –∫ OpenAI. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –∏–Ω—Ç–µ—Ä–Ω–µ—Ç-—Å–æ–µ–¥–∏–Ω–µ–Ω–∏–µ.";
      } else if (error.message.includes('timeout')) {
        errorTitle = "‚è±Ô∏è –¢–∞–π–º–∞—É—Ç";
        errorDescription = "–ó–∞–ø—Ä–æ—Å –∫ OpenAI –∑–∞–Ω—è–ª —Å–ª–∏—à–∫–æ–º –º–Ω–æ–≥–æ –≤—Ä–µ–º–µ–Ω–∏. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.";
      }

      updateItemState(enItem.id, { 
        timingsText: `‚ùå ${errorDescription}`, 
        isTranslatingTimings: false 
      });

      toast({ 
        title: errorTitle, 
        description: errorDescription, 
        variant: "destructive",
        duration: 8000  // Show error longer
      });
    }
  }, [filesToProcess, updateItemState, currentLanguage, toast]);

  useEffect(() => {
    filesToProcess.forEach(item => {
      if (item.lang === 'es' && item.timingsText && !item.translationTriggered) {
        const enItem = filesToProcess.find(i => i.originalFileId === item.originalFileId && i.lang === 'en');
        if (enItem) {
          updateItemState(item.id, { translationTriggered: true });
          handleTranslateTimings(item.id);
        }
      }
    });
  }, [filesToProcess, handleTranslateTimings, updateItemState]);


  useEffect(() => {
    return () => {
      Object.values(pollingIntervals.current).forEach(clearInterval);
    };
  }, []);

  const addFilesToQueue = useCallback(async (acceptedFiles) => {
    const newItemsPromises = acceptedFiles.flatMap(async (file) => {
      const nameWithoutExt = getFileNameWithoutExtension(file.name);
      const langSuffixMatch = nameWithoutExt.match(/_([RUruESesENen]{2})$/i);

      if (langSuffixMatch) {
        const lang = langSuffixMatch[1].toLowerCase();
        if (lang === 'es') {
           return Promise.all([
            generateInitialItemData(file, 'es', currentLanguage, toast),
            generateInitialItemData(file, 'en', currentLanguage, toast, 'es') 
          ]);
        }
        return [await generateInitialItemData(file, lang, currentLanguage, toast)];
      } else {
        const esDataPromise = generateInitialItemData(file, 'es', currentLanguage, toast);
        const ruDataPromise = generateInitialItemData(file, 'ru', currentLanguage, toast);

        const esData = await esDataPromise;
        const enDataPromise = generateInitialItemData(file, 'en', currentLanguage, toast, 'es', esData?.timingsText);

        return Promise.all([ruDataPromise, esDataPromise, enDataPromise]);
      }
    });

    const newItemsArrays = await Promise.all(newItemsPromises);
    const newItemsFlat = newItemsArrays.flat();
    setFilesToProcess(prev => [...prev, ...newItemsFlat.filter(item => item !== null)]);
  }, [currentLanguage, toast]);

  const processSingleItem = useCallback(async (itemData, forceOverwrite = false) => {
    return processSingleItemService({
        itemData,
        forceOverwrite,
        updateItemState,
        currentLanguage,
        toast,
        openOverwriteDialog,
        pollingIntervalsRef: pollingIntervals,
        getAllItems: () => filesToProcess 
    });
  }, [updateItemState, currentLanguage, toast, openOverwriteDialog, filesToProcess]);


  const handleProcessAllFiles = useCallback(async () => {
    if (isDialogOpen.current) return;
    setIsProcessingAll(true);

    let encounteredDialogInThisRun = false;

    for (const item of filesToProcess) {
      if (isDialogOpen.current) {
        encounteredDialogInThisRun = true;
        break; 
      }
      if (!item.uploadComplete && !item.uploadError && !item.isUploading) { 
        const result = await processSingleItem(item);
        if (!result.success && result.requiresDialog) {
            encounteredDialogInThisRun = true;
            break; 
        }
      }
    }

    if (!encounteredDialogInThisRun) {
        setIsProcessingAll(false);
        const allProcessedSuccessfully = filesToProcess.every(f => f.uploadComplete || f.uploadError);
        const someFailed = filesToProcess.some(f => f.uploadError);

        if (filesToProcess.length > 0) {
            if (allProcessedSuccessfully && !someFailed) {
                 toast({ title: getLocaleString('allFilesProcessedTitle', currentLanguage), description: getLocaleString('allFilesProcessedDesc', currentLanguage), duration: 5000 });
            } else if (someFailed) {
                 toast({ title: getLocaleString('processingErrorsTitle', currentLanguage), description: getLocaleString('processingErrorsDesc', currentLanguage), variant: "destructive", duration: 7000 });
            }
        }
    }
  }, [filesToProcess, processSingleItem, currentLanguage, toast]);


  const handleTimingsChange = (itemUniqueId, newText) => {
    setFilesToProcess(prev => {
        const updatedFiles = prev.map(item => item.id === itemUniqueId ? { ...item, timingsText: newText, translationTriggered: false } : item);
        const changedItem = updatedFiles.find(item => item.id === itemUniqueId);

        if (changedItem && changedItem.lang === 'es' && newText.trim()) {
            const enItem = updatedFiles.find(item => item.originalFileId === changedItem.originalFileId && item.lang === 'en');
            if (enItem) {
                handleTranslateTimings(changedItem.id);
            }
        }
        return updatedFiles;
    });
  };


  const handleTitleChange = (itemUniqueId, newTitle) => {
     setFilesToProcess(prev => prev.map(item => item.id === itemUniqueId ? { ...item, episodeTitle: newTitle } : item));
  };

  const handleRemoveItem = (itemIdToRemove) => {
     setFilesToProcess(prev => {
        const itemToRemove = prev.find(item => item.id === itemIdToRemove);
        if (itemToRemove && pollingIntervals.current[itemToRemove.id]) {
            clearInterval(pollingIntervals.current[itemToRemove.id]);
            delete pollingIntervals.current[itemToRemove.id];
        }
        return prev.filter(item => item.id !== itemIdToRemove);
     });
  };

  const confirmOverwrite = async () => {
    const itemToProcess = currentItemForOverwrite;
    const resolvePromise = overwritePromiseResolve.current;

    closeOverwriteDialog();

    if (itemToProcess && resolvePromise) {
      updateItemState(itemToProcess.id, { uploadError: null, isUploading: true, uploadProgress: 0, uploadComplete: false, transcriptionStatus: null, transcriptionError: null }); 
      const result = await processSingleItem(itemToProcess, true);
      if (typeof resolvePromise === 'function') {
        resolvePromise(result.success);
      }

      if (isProcessingAll) {
         setIsProcessingAll(false); 
         handleProcessAllFiles(); 
      }
    } else if (isProcessingAll) {
        setIsProcessingAll(false); 
    }
  };

  const cancelOverwrite = () => {
    const itemToCancel = currentItemForOverwrite;
    const resolvePromise = overwritePromiseResolve.current;

    closeOverwriteDialog();

    if (itemToCancel) {
      updateItemState(itemToCancel.id, { isUploading: false, uploadError: getLocaleString('uploadCancelledEpisodeExists', currentLanguage) });
    }
    if (resolvePromise && typeof resolvePromise === 'function') {
        resolvePromise(false);
    }

    if (isProcessingAll) {
        setIsProcessingAll(false);
    }
  };

  return {
    filesToProcess,
    isProcessingAll,
    showOverwriteDialog,
    currentItemForOverwrite,
    addFilesToQueue,
    handleProcessAllFiles,
    handleTimingsChange,
    handleTitleChange,
    handleRemoveItem,
    confirmOverwrite,
    cancelOverwrite,
  };
};

export default useFileUploadManager;

import { useState, useCallback } from 'react';

const useQuestionManagement = (
  currentTime,
  currentLanguage,
  audioRef,
  mainPlayerSeekAudio,
  duration,
  episodeSlug, 
  episodeDate,
  episodeLang // Added episodeLang
) => {
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isBatchAddDialogOpen, setIsBatchAddDialogOpen] = useState(false);
  const [currentQuestionData, setCurrentQuestionData] = useState(null);
  const [isAddingQuestion, setIsAddingQuestion] = useState(false);
  const [dialogTime, setDialogTime] = useState(0);
  const [questionLang, setQuestionLang] = useState(currentLanguage);
  const [batchQuestionsText, setBatchQuestionsText] = useState('');
  const [initialDialogTimeSet, setInitialDialogTimeSet] = useState(false);
  const [isAddQuestionFromSegmentDialogOpen, setIsAddQuestionFromSegmentDialogOpen] = useState(false);
  const [segmentForQuestion, setSegmentForQuestion] = useState(null);


  const openEditDialog = useCallback((question) => {
    let langForDialog = question.lang || currentLanguage;
    if (currentLanguage === 'en' && episodeLang === 'es' && question.lang !== 'en') {
      langForDialog = 'en';
    }

    setCurrentQuestionData({ ...question, time: parseFloat(question.time.toFixed(2)) });
    setDialogTime(parseFloat(question.time.toFixed(2)));
    setQuestionLang(langForDialog);
    setIsAddingQuestion(false);
    setIsEditDialogOpen(true);
    setInitialDialogTimeSet(true);
  }, [currentLanguage, episodeLang]);

  const openAddDialog = useCallback(() => {
    const newTime = parseFloat(audioRef.current?.currentTime.toFixed(2) || currentTime.toFixed(2));
    let langForNewQuestion = currentLanguage;
    if (currentLanguage === 'en' && episodeLang === 'es') {
      langForNewQuestion = 'en';
    }
    setCurrentQuestionData({ id: null, time: newTime, title: '', episode_slug: episodeSlug, lang: langForNewQuestion });
    setDialogTime(newTime);
    setQuestionLang(langForNewQuestion);
    setIsAddingQuestion(true);
    setIsEditDialogOpen(true);
    setInitialDialogTimeSet(false);
  }, [currentTime, currentLanguage, audioRef, episodeSlug, episodeLang]);

  const openBatchAddDialog = useCallback(() => {
    setIsBatchAddDialogOpen(true);
  }, []);

  const openAddQuestionFromSegmentDialog = useCallback((segment) => {
    setSegmentForQuestion(segment);
    setIsAddQuestionFromSegmentDialogOpen(true);
  }, []);

  const closeDialog = useCallback(() => {
    setIsEditDialogOpen(false);
    setInitialDialogTimeSet(false);
  }, []);

  const closeBatchAddDialog = useCallback(() => {
    setIsBatchAddDialogOpen(false);
    setBatchQuestionsText('');
  }, []);

  const closeAddQuestionFromSegmentDialog = useCallback(() => {
    setIsAddQuestionFromSegmentDialogOpen(false);
    setSegmentForQuestion(null);
  }, []);

  const handleDialogTimeInputChange = useCallback((e) => {
    const parts = e.target.value.split(':').map(Number);
    let newTimeValue = 0;
    if (parts.length === 3) newTimeValue = parts[0] * 3600 + parts[1] * 60 + parts[2];
    else if (parts.length === 2) newTimeValue = parts[0] * 60 + parts[1];
    else if (parts.length === 1 && !isNaN(parts[0])) newTimeValue = parts[0];
    else { setDialogTime(NaN); return; }

    if (!isNaN(newTimeValue) && newTimeValue >= 0 && newTimeValue <= (duration || Infinity)) {
      setDialogTime(newTimeValue);
      setCurrentQuestionData(prev => ({ ...prev, time: newTimeValue }));
      if (mainPlayerSeekAudio) mainPlayerSeekAudio(newTimeValue, false);
    } else if (e.target.value === "") {
      setDialogTime(0);
      setCurrentQuestionData(prev => ({ ...prev, time: 0 }));
    }
  }, [duration, mainPlayerSeekAudio]);

  const handleDialogTimeAdjust = useCallback((amount) => {
    const newTime = Math.max(0, Math.min(duration || Infinity, dialogTime + amount));
    setDialogTime(newTime);
    setCurrentQuestionData(prev => ({ ...prev, time: newTime }));
    if (mainPlayerSeekAudio) mainPlayerSeekAudio(newTime, true);
  }, [dialogTime, duration, mainPlayerSeekAudio]);

  return {
    isEditDialogOpen,
    isBatchAddDialogOpen,
    currentQuestionData,
    isAddingQuestion,
    dialogTime,
    questionLang,
    batchQuestionsText,
    isAddQuestionFromSegmentDialogOpen,
    segmentForQuestion,
    openEditDialog,
    openAddDialog,
    openBatchAddDialog,
    closeDialog,
    closeBatchAddDialog,
    handleDialogTimeInputChange,
    handleDialogTimeAdjust,
    setCurrentQuestionData,
    setBatchQuestionsText,
    setDialogTime,
    setQuestionLang,
    openAddQuestionFromSegmentDialog,
    closeAddQuestionFromSegmentDialog,
  };
};

