import React, { useState, useRef, useCallback } from 'react';
import { useToast } from '@/components/ui/use-toast';
import { getLocaleString } from '@/lib/locales';
import logService from '@/lib/logService';

const useSegmentEditing = (
  utterances, 
  onSaveEditedSegment, 
  audioRef, 
  currentLanguage,
  user,
  episodeSlug
) => {
  const [editingSegment, setEditingSegment] = useState(null);
  const [editedText, setEditedText] = useState('');
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);
  const [confirmDialogProps, setConfirmDialogProps] = useState({});
  const [textareaRef, setTextareaRef] = useState(null);
  const [isSaving, setIsSaving] = useState(false);
  const initialAudioState = useRef({ isPlaying: false, currentTime: 0 });
  const { toast } = useToast();

  const handleEditSegment = useCallback((segmentToEdit) => {
    if (audioRef?.current) {
      initialAudioState.current = {
        isPlaying: !audioRef.current.paused,
        currentTime: audioRef.current.currentTime,
      };
    }
    setEditingSegment(segmentToEdit);
    setEditedText(segmentToEdit ? segmentToEdit.text : '');
  }, [audioRef]);

  const restoreAudioState = useCallback(() => {
    if (audioRef?.current && initialAudioState.current.isPlaying && audioRef.current.paused) {
      audioRef.current.currentTime = initialAudioState.current.currentTime;
      audioRef.current.play().catch(e => console.error("Error restoring audio play state:", e));
    } else if (audioRef?.current) {
      audioRef.current.currentTime = initialAudioState.current.currentTime;
    }
  }, [audioRef]);

  const handleSaveCurrentSegmentEdit = useCallback(async () => {
    if (!editingSegment || isSaving) return;

    setIsSaving(true);
    try {
      const updatedSegment = { ...editingSegment, text: editedText };
      const newUtterances = utterances.map(utt =>
        (utt.id || utt.start) === (editingSegment.id || editingSegment.start) ? updatedSegment : utt
      );
      const originalSegment = utterances.find(utt => (utt.id || utt.start) === (editingSegment.id || editingSegment.start));

      await onSaveEditedSegment(newUtterances, 'update', originalSegment, updatedSegment);

      setEditingSegment(null);
      toast({
        title: getLocaleString('transcriptSegmentUpdatedTitle', currentLanguage),
        description: getLocaleString('transcriptSegmentUpdatedDesc', currentLanguage),
        className: "bg-green-600/80 border-green-500 text-white"
      });
      restoreAudioState();
    } catch (error) {
      console.error("Failed to save segment:", error);
      toast({ title: "Save Error", description: error.message, variant: "destructive" });
    } finally {
      setIsSaving(false);
    }
  }, [editingSegment, isSaving, editedText, utterances, onSaveEditedSegment, toast, currentLanguage, restoreAudioState]);

  const handleCancelEdit = useCallback(() => {
    setEditingSegment(null);
    restoreAudioState();
  }, [restoreAudioState]);

  const executeAction = useCallback(async (actionType, segmentToModify, textContent, cursorPos) => {
    let newUtterances;
    const originalUtterances = JSON.parse(JSON.stringify(utterances)); 
    let logEntityId = segmentToModify.id || segmentToModify.start;
    let actionDetail = {};

    switch (actionType) {
      case 'Split': {
        if (textareaRef?.current && textareaRef.current.selectionStart !== undefined) {
          cursorPos = textareaRef.current.selectionStart;
        } else if (cursorPos === undefined || cursorPos === null) {
          toast({ title: getLocaleString('errorSplittingSegment', currentLanguage), description: getLocaleString('cursorPositionRequired', currentLanguage), variant: 'destructive' });
          return;
        }
        const textBefore = textContent.substring(0, cursorPos).trim();
        const textAfter = textContent.substring(cursorPos).trim();
        if (!textBefore || !textAfter || !segmentToModify.words || segmentToModify.words.length === 0) {
          toast({ title: getLocaleString('errorSplittingSegment', currentLanguage), description: getLocaleString('cannotSplitEmpty', currentLanguage), variant: 'destructive' });
          return;
        }
        let splitWordIndex = -1;
        let cumulativeLength = 0;
        for (let i = 0; i < segmentToModify.words.length; i++) {
          cumulativeLength += segmentToModify.words[i].text.length + (i > 0 ? 1 : 0);
          if (cumulativeLength >= cursorPos) {
            splitWordIndex = i;
            break;
          }
        }
        if (splitWordIndex === -1 || splitWordIndex >= segmentToModify.words.length - 1) {
          toast({ title: getLocaleString('errorSplittingSegment', currentLanguage), description: getLocaleString('cannotSplitAtPosition', currentLanguage), variant: 'destructive' });
          return;
        }
        const wordsBefore = segmentToModify.words.slice(0, splitWordIndex + 1);
        const wordsAfter = segmentToModify.words.slice(splitWordIndex + 1);
        if (wordsBefore.length === 0 || wordsAfter.length === 0) {
          toast({ title: getLocaleString('errorSplittingSegment', currentLanguage), description: getLocaleString('cannotSplitIntoEmpty', currentLanguage), variant: 'destructive' });
          return;
        }
        const newSegment1 = { ...segmentToModify, text: textBefore, end: wordsBefore[wordsBefore.length - 1].end, words: wordsBefore, id: segmentToModify.id || `${segmentToModify.start}-split1-${Date.now()}` };
        const newSegment2 = { ...segmentToModify, start: wordsAfter[0].start, text: textAfter, words: wordsAfter, id: `${segmentToModify.start}-split2-${Date.now()}` };
        const segmentIndexInAllUtterances = utterances.findIndex(utt => (utt.id || utt.start) === (segmentToModify.id || segmentToModify.start));
        if (segmentIndexInAllUtterances === -1) return;
        newUtterances = [...utterances.slice(0, segmentIndexInAllUtterances), newSegment1, newSegment2, ...utterances.slice(segmentIndexInAllUtterances + 1)];
        actionDetail = { splitAt: cursorPos, segment1: newSegment1, segment2: newSegment2 };
        break;
      }
      case 'Merge': {
        const currentSegmentIndex = utterances.findIndex(utt => (utt.id || utt.start) === (segmentToModify.id || segmentToModify.start));
        if (currentSegmentIndex <= 0) {
          toast({ title: getLocaleString('errorMergingSegment', currentLanguage), description: getLocaleString('cannotMergeFirstSegment', currentLanguage), variant: 'destructive' });
          return;
        }
        const previousSegment = utterances[currentSegmentIndex - 1];
        const mergedText = `${previousSegment.text} ${segmentToModify.text}`.trim();
        const mergedWords = [...(previousSegment.words || []), ...(segmentToModify.words || [])].sort((a, b) => a.start - b.start);
        const mergedSegment = { ...previousSegment, text: mergedText, end: segmentToModify.end, words: mergedWords, id: previousSegment.id || `${previousSegment.start}-merged-${Date.now()}` };
        newUtterances = [...utterances.slice(0, currentSegmentIndex - 1), mergedSegment, ...utterances.slice(currentSegmentIndex + 1)];
        actionDetail = { mergedWith: previousSegment.id || previousSegment.start, resultingSegment: mergedSegment };
        break;
      }
      case 'Delete': {
        newUtterances = utterances.filter(utt => (utt.id || utt.start) !== (segmentToModify.id || segmentToModify.start));
        actionDetail = { deletedSegment: segmentToModify };
        break;
      }
      default:
        return;
    }
    if (newUtterances) {
      await onSaveEditedSegment(newUtterances, actionType.toLowerCase(), segmentToModify, newUtterances.find(utt => utt.id === (actionDetail.resultingSegment?.id || actionDetail.segment1?.id) ) || null, originalUtterances);
      setEditingSegment(null);
      restoreAudioState();
    }
  }, [utterances, onSaveEditedSegment, toast, currentLanguage, restoreAudioState, textareaRef, user, episodeSlug]);

  const performActionWithConfirmation = useCallback((actionType, segmentToModify, textContent, cursorPos) => {
    const dontAskAgainKey = `confirm${actionType}SegmentDisabled`;
    const isDisabled = localStorage.getItem(dontAskAgainKey) === 'true';

    if (isDisabled) {
      executeAction(actionType, segmentToModify, textContent, cursorPos);
      return;
    }

    let titleKey, descriptionKey;
    switch (actionType) {
      case 'Split': titleKey = 'confirmSplitTitle'; descriptionKey = 'confirmSplitDescription'; break;
      case 'Merge': titleKey = 'confirmMergeTitle'; descriptionKey = 'confirmMergeDescription'; break;
      case 'Delete': titleKey = 'confirmDeleteSegmentTitle'; descriptionKey = 'confirmDeleteSegmentDescription'; break;
      default: return;
    }

    setConfirmDialogProps({
      title: getLocaleString(titleKey, currentLanguage),
      description: getLocaleString(descriptionKey, currentLanguage),
      onConfirm: () => {
        executeAction(actionType, segmentToModify, textContent, cursorPos);
        setShowConfirmDialog(false);
      },
      onCancel: () => setShowConfirmDialog(false),
      actionType
    });
    setShowConfirmDialog(true);
  }, [executeAction, currentLanguage]);

  return {
    editingSegment,
    setEditingSegment,
    editedText,
    setEditedText,
    showConfirmDialog,
    confirmDialogProps,
    textareaRef, 
    setTextareaRef,
    handleEditSegment,
    handleSaveCurrentSegmentEdit,
    handleCancelEdit,
    performActionWithConfirmation,
    setShowConfirmDialog,
    isSaving,
  };
};

export default useSegmentEditing;

import { useState, useEffect, useCallback, useRef } from 'react';
import { useToast } from '@/components/ui/use-toast';
import useTranscriptData from '@/hooks/transcript/useTranscriptData';
import useTranscriptAssemblyAI from '@/hooks/transcript/useTranscriptAssemblyAI';
import useTranscriptPlayerSync from '@/hooks/transcript/useTranscriptPlayerSync';
import { supabase } from '@/lib/supabaseClient';
import { getLocaleString } from '@/lib/locales';
import { processTranscriptData } from './transcript/transcriptProcessingUtils';

const useTranscript = (episodeSlug, episodeAudioUrl, episodeLang, currentLanguage, audioRef, externalTranscriptUtterances) => {
  const { toast } = useToast();

  const {
    transcript,
    setTranscript,
    transcriptDbId,
    setTranscriptDbId,
    isLoadingTranscript,
    setIsLoadingTranscript,
    transcriptError,
    setTranscriptError,
  } = useTranscriptData(externalTranscriptUtterances);

  const {
    isPollingTranscript,
    setIsPollingTranscript,
    transcriptionJobId,
    setTranscriptionJobId,
    pollingTimeoutRef,
    determineAssemblyLangForEpisode,
    pollTranscriptStatus,
    handleStartTranscription,
  } = useTranscriptAssemblyAI(
    episodeSlug, 
    episodeAudioUrl, 
    episodeLang, 
    currentLanguage, 
    toast, 
    setTranscript, 
    setTranscriptDbId, 
    setIsLoadingTranscript, 
    setTranscriptError,
    transcript,
    transcriptDbId
  );

  const {
    activeSegmentTime,
    setActiveSegmentTime,
    segmentPlaying,
    setSegmentPlaying,
  } = useTranscriptPlayerSync(audioRef, transcript);

  const fetchTranscript = useCallback(async () => {
    if (!episodeSlug) return;
    setIsLoadingTranscript(true);
    setTranscriptError(null);
    try {
      const transcriptLangForQuery = episodeLang === 'all' ? currentLanguage : episodeLang;

      const { data, error: fetchError } = await supabase
        .from('transcripts')
        .select('id, episode_slug, lang, assemblyai_transcript_id, status, transcript_data, edited_transcript_data')
        .eq('episode_slug', episodeSlug)
        .eq('lang', transcriptLangForQuery)
        .order('created_at', { ascending: false })
        .limit(1)
        .maybeSingle(); 

      if (fetchError && fetchError.code !== 'PGRST116') throw fetchError;

      if (data) {
        setTranscriptDbId(data.id);
        setTranscriptionJobId(data.assemblyai_transcript_id);

        const displayData = data.edited_transcript_data || data.transcript_data;
        setTranscript(displayData); // processTranscriptData is now inside useTranscriptData's setTranscript

        if (data.status === 'completed' && displayData) {
          // Already set by setTranscript
        } else if (data.status === 'processing' || data.status === 'queued') {
          toast({ title: getLocaleString('transcriptionInProgressTitle', currentLanguage), description: getLocaleString('transcriptionInProgressDescription', currentLanguage) });
          const assemblyLangForPolling = data.lang === 'ru' ? 'ru' : (data.lang === 'es' ? 'es' : determineAssemblyLangForEpisode());
          if (pollingTimeoutRef.current) clearTimeout(pollingTimeoutRef.current);
          pollTranscriptStatus(data.assemblyai_transcript_id, data.id, assemblyLangForPolling);
        } else if (data.status === 'error') {
            const errorMessage = data.transcript_data?.error || getLocaleString('unknownError', currentLanguage);
            setTranscriptError(getLocaleString('transcriptionError', currentLanguage) + `: ${errorMessage}`);
            toast({ title: getLocaleString('transcriptionErrorTitle', currentLanguage), description: getLocaleString('transcriptionError', currentLanguage) + `: ${errorMessage}`, variant: 'destructive' });
            setTranscript(null);
        } else {
          setTranscript(null); 
          setTranscriptError(getLocaleString('noTranscriptAvailableYet', currentLanguage));
        }
      } else {
        setTranscript(null);
        setTranscriptError(getLocaleString('noTranscriptAvailableYet', currentLanguage));
      }
    } catch (err) {
      if (err.code !== 'PGRST116') { 
        setTranscriptError(getLocaleString('errorFetchingTranscript', currentLanguage, { errorMessage: err.message }));
        toast({ title: getLocaleString('errorGeneric', currentLanguage), description: err.message, variant: 'destructive' });
      } else {
         setTranscript(null);
         setTranscriptError(getLocaleString('noTranscriptAvailableYet', currentLanguage));
      }
    } finally {
      setIsLoadingTranscript(false);
    }
  }, [
      episodeSlug, 
      episodeLang, 
      currentLanguage, 
      setIsLoadingTranscript, 
      setTranscriptError, 
      setTranscriptDbId, 
      setTranscriptionJobId, 
      setTranscript, 
      toast, 
      determineAssemblyLangForEpisode, 
      pollTranscriptStatus,
      pollingTimeoutRef
    ]);

  useEffect(() => {
    if (!externalTranscriptUtterances) { // Only fetch if not provided externally (e.g. from PlayerPage)
        fetchTranscript();
    }
    return () => {
        if (pollingTimeoutRef.current) {
            clearTimeout(pollingTimeoutRef.current);
        }
    }
  }, [fetchTranscript, externalTranscriptUtterances]);


  const handleSaveEditedSegment = async (updatedUtterances) => {
    if (!transcript) return;
    // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ —Ä–∞–∑–±–∏–≤–∞–µ–º –¥–ª–∏–Ω–Ω—ã–µ —Å–µ–≥–º–µ–Ω—Ç—ã
    const processedTranscript = processTranscriptData({ ...transcript, utterances: updatedUtterances, words: transcript.words });
    const newEditedTranscriptData = { 
      ...transcript, 
      utterances: processedTranscript.utterances,
      text: processedTranscript.utterances.map(u => u.text).join(' '),
      words: transcript.words || [] 
    };
    setTranscript(newEditedTranscriptData);
    try {
      const { error: updateError } = await supabase
        .from('transcripts')
        .update({ edited_transcript_data: newEditedTranscriptData })
        .eq('id', transcriptDbId);
      if (updateError) {
        console.error("Error updating segment in DB:", updateError);
        toast({ title: getLocaleString('errorGeneric', currentLanguage), description: getLocaleString('errorUpdatingSegment', currentLanguage, { errorMessage: updateError.message }), variant: 'destructive' });
        fetchTranscript(); 
      } else {
        toast({ title: getLocaleString('transcriptSegmentUpdatedTitle', currentLanguage), description: getLocaleString('transcriptSegmentUpdatedDesc', currentLanguage), variant: "default" });
      }
    } catch (err) {
      toast({ title: getLocaleString('errorGeneric', currentLanguage), description: getLocaleString('errorUpdatingSegment', currentLanguage, { errorMessage: err.message }), variant: 'destructive' });
      fetchTranscript(); 
    }
  };

  return {
    transcript,
    isLoadingTranscript,
    transcriptError,
    isPollingTranscript,
    fetchTranscript,
    handleStartTranscription,
    handleSaveEditedSegment,
    determineAssemblyLangForEpisode,
    activeSegmentTime,
    setActiveSegmentTime,
    segmentPlaying,
    setSegmentPlaying,
  };
};

export default useTranscript;

import offlineDataService from './offlineDataService';

class AudioCacheService {
  constructor() {
    this.cacheInProgress = new Set();
    this.maxCacheSize = 200 * 1024 * 1024; // 200MB –º–∞–∫—Å–∏–º—É–º
    this.downloadListeners = [];
  }

  // –ü–æ–¥–ø–∏—Å–∫–∞ –Ω–∞ —Å–æ–±—ã—Ç–∏—è –∑–∞–≥—Ä—É–∑–∫–∏
  onDownloadProgress(callback) {
    this.downloadListeners.push(callback);
    return () => {
      this.downloadListeners = this.downloadListeners.filter(cb => cb !== callback);
    };
  }

  // –£–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ —Å–ª—É—à–∞—Ç–µ–ª–µ–π –æ –ø—Ä–æ–≥—Ä–µ—Å—Å–µ
  notifyDownloadListeners(event, data = {}) {
    this.downloadListeners.forEach(callback => {
      try {
        callback(event, data);
      } catch (error) {
        console.error('[AudioCache] Download listener error:', error);
      }
    });
  }

  // –ü—Ä–æ–≤–µ—Ä–∫–∞, –µ—Å—Ç—å –ª–∏ –∞—É–¥–∏–æ –≤ –∫–µ—à–µ
  async isAudioCached(url) {
    try {
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –≤ Service Worker –∫–µ—à–µ
      if ('caches' in window) {
        const cache = await caches.open('audio-v1');
        const response = await cache.match(url);
        if (response) {
          return true;
        }
      }

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –º–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ –≤ IndexedDB
      await offlineDataService.init();
      const metadata = await offlineDataService.getAudioFileMetadata(url);
      return !!metadata;
    } catch (error) {
      console.error('[AudioCache] Error checking cache:', error);
      return false;
    }
  }

  // –ü–æ–ª—É—á–µ–Ω–∏–µ —Ä–∞–∑–º–µ—Ä–∞ –∫–µ—à–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ –∞—É–¥–∏–æ
  async getCachedAudioSize(url) {
    try {
      await offlineDataService.init();
      const metadata = await offlineDataService.getAudioFileMetadata(url);
      return metadata ? metadata.size : 0;
    } catch (error) {
      console.error('[AudioCache] Error getting cached audio size:', error);
      return 0;
    }
  }

  // –ö–µ—à–∏—Ä–æ–≤–∞–Ω–∏–µ –∞—É–¥–∏–æ—Ñ–∞–π–ª–∞
  async cacheAudio(url, episodeSlug, forceRedownload = false) {
    // –ü—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–∞–µ–º –º–Ω–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω—ã–µ –∑–∞–≥—Ä—É–∑–∫–∏ –æ–¥–Ω–æ–≥–æ —Ñ–∞–π–ª–∞
    if (this.cacheInProgress.has(url)) {
      console.log('[AudioCache] Already caching:', url);
      return false;
    }

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ —É–∂–µ –≤ –∫–µ—à–µ
    if (!forceRedownload && await this.isAudioCached(url)) {
      console.log('[AudioCache] Already cached:', url);
      await offlineDataService.updateAudioFileAccess(url);
      return true;
    }

    this.cacheInProgress.add(url);

    try {
      await offlineDataService.init();

      this.notifyDownloadListeners('download_start', { url, episodeSlug });

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –¥–æ—Å—Ç—É–ø–Ω–æ–µ –º–µ—Å—Ç–æ
      const storageEstimate = await offlineDataService.getStorageUsage();
      const availableSpace = storageEstimate.quota - storageEstimate.usage;

      if (availableSpace < 50 * 1024 * 1024) { // –ú–∏–Ω–∏–º—É–º 50MB —Å–≤–æ–±–æ–¥–Ω–æ–≥–æ –º–µ—Å—Ç–∞
        throw new Error('Insufficient storage space');
      }

      // –ó–∞–≥—Ä—É–∂–∞–µ–º —Ñ–∞–π–ª —Å –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ–º –ø—Ä–æ–≥—Ä–µ—Å—Å–∞
      const response = await this.fetchWithProgress(url, (loaded, total) => {
        const progress = total > 0 ? Math.round((loaded / total) * 100) : 0;
        this.notifyDownloadListeners('download_progress', {
          url,
          episodeSlug,
          loaded,
          total,
          progress
        });
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const contentLength = response.headers.get('content-length');
      const fileSize = contentLength ? parseInt(contentLength) : 0;

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Ä–∞–∑–º–µ—Ä —Ñ–∞–π–ª–∞
      if (fileSize > this.maxCacheSize * 0.5) { // –ù–µ –±–æ–ª–µ–µ 50% –æ—Ç –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–≥–æ —Ä–∞–∑–º–µ—Ä–∞ –∫–µ—à–∞
        throw new Error('File too large for caching');
      }

      // –ö–µ—à–∏—Ä—É–µ–º –≤ Service Worker
      if ('caches' in window) {
        const cache = await caches.open('audio-v1');

        // –£–ø—Ä–∞–≤–ª—è–µ–º —Ä–∞–∑–º–µ—Ä–æ–º –∫–µ—à–∞
        await this.manageCacheSize(cache, fileSize);

        // –°–æ—Ö—Ä–∞–Ω—è–µ–º –≤ –∫–µ—à
        await cache.put(url, response.clone());
        console.log('[AudioCache] Cached in SW:', url);
      }

      // –°–æ—Ö—Ä–∞–Ω—è–µ–º –º–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ –≤ IndexedDB
      await offlineDataService.saveAudioFileMetadata(url, episodeSlug, fileSize);

      this.notifyDownloadListeners('download_complete', {
        url,
        episodeSlug,
        size: fileSize
      });

      console.log('[AudioCache] Successfully cached audio:', url);
      return true;

    } catch (error) {
      console.error('[AudioCache] Failed to cache audio:', error);

      this.notifyDownloadListeners('download_error', {
        url,
        episodeSlug,
        error: error.message
      });

      throw error;
    } finally {
      this.cacheInProgress.delete(url);
    }
  }

  // –ó–∞–≥—Ä—É–∑–∫–∞ —Å –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ–º –ø—Ä–æ–≥—Ä–µ—Å—Å–∞
  async fetchWithProgress(url, onProgress) {
    const response = await fetch(url);

    if (!response.body) {
      return response;
    }

    const contentLength = response.headers.get('content-length');
    const total = contentLength ? parseInt(contentLength) : 0;
    let loaded = 0;

    const reader = response.body.getReader();
    const chunks = [];

    while (true) {
      const { done, value } = await reader.read();

      if (done) break;

      chunks.push(value);
      loaded += value.length;

      if (onProgress) {
        onProgress(loaded, total);
      }
    }

    // –°–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π Response –∏–∑ —Å–æ–±—Ä–∞–Ω–Ω—ã—Ö —á–∞–Ω–∫–æ–≤
    const allChunks = new Uint8Array(loaded);
    let position = 0;

    for (const chunk of chunks) {
      allChunks.set(chunk, position);
      position += chunk.length;
    }

    return new Response(allChunks, {
      status: response.status,
      statusText: response.statusText,
      headers: response.headers
    });
  }

  // –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Ä–∞–∑–º–µ—Ä–æ–º –∫–µ—à–∞
  async manageCacheSize(cache, newFileSize) {
    try {
      const keys = await cache.keys();
      let totalSize = newFileSize;
      const filesWithSize = [];

      // –ü–æ–¥—Å—á–∏—Ç—ã–≤–∞–µ–º —Ç–µ–∫—É—â–∏–π —Ä–∞–∑–º–µ—Ä –∫–µ—à–∞
      for (const request of keys) {
        const response = await cache.match(request);
        if (response) {
          const size = await this.getResponseSize(response);
          totalSize += size;
          filesWithSize.push({
            request,
            size,
            url: request.url
          });
        }
      }

      // –ï—Å–ª–∏ –ø—Ä–µ–≤—ã—à–∞–µ–º –ª–∏–º–∏—Ç, —É–¥–∞–ª—è–µ–º —Å—Ç–∞—Ä—ã–µ —Ñ–∞–π–ª—ã
      if (totalSize > this.maxCacheSize) {
        console.log('[AudioCache] Cache size exceeded, cleaning up...');

        // –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ –≤—Ä–µ–º–µ–Ω–∏ –ø–æ—Å–ª–µ–¥–Ω–µ–≥–æ –¥–æ—Å—Ç—É–ø–∞
        await offlineDataService.init();
        const sortedFiles = [];

        for (const file of filesWithSize) {
          const metadata = await offlineDataService.getAudioFileMetadata(file.url);
          sortedFiles.push({
            ...file,
            lastAccessed: metadata ? metadata.last_accessed : 0
          });
        }

        // –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ –≤—Ä–µ–º–µ–Ω–∏ –ø–æ—Å–ª–µ–¥–Ω–µ–≥–æ –¥–æ—Å—Ç—É–ø–∞ (—Å—Ç–∞—Ä—ã–µ —Å–Ω–∞—á–∞–ª–∞)
        sortedFiles.sort((a, b) => a.lastAccessed - b.lastAccessed);

        // –£–¥–∞–ª—è–µ–º —Ñ–∞–π–ª—ã –ø–æ–∫–∞ –Ω–µ –æ—Å–≤–æ–±–æ–¥–∏–º –¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –º–µ—Å—Ç–∞
        let freedSpace = 0;
        const targetSpace = this.maxCacheSize * 0.3; // –û—Å–≤–æ–±–æ–∂–¥–∞–µ–º 30% –º–µ—Å—Ç–∞

        for (const file of sortedFiles) {
          if (freedSpace >= targetSpace) break;

          await cache.delete(file.request);
          await offlineDataService.getTransaction(['audioFiles'], 'readwrite')
            .objectStore('audioFiles')
            .delete(file.url);

          freedSpace += file.size;
          console.log('[AudioCache] Removed from cache:', file.url);
        }
      }
    } catch (error) {
      console.error('[AudioCache] Error managing cache size:', error);
    }
  }

  // –ü–æ–ª—É—á–µ–Ω–∏–µ —Ä–∞–∑–º–µ—Ä–∞ –æ—Ç–≤–µ—Ç–∞
  async getResponseSize(response) {
    try {
      const contentLength = response.headers.get('content-length');
      if (contentLength) {
        return parseInt(contentLength);
      }

      // –ï—Å–ª–∏ –Ω–µ—Ç –∑–∞–≥–æ–ª–æ–≤–∫–∞ content-length, –∫–ª–æ–Ω–∏—Ä—É–µ–º –∏ —Å—á–∏—Ç–∞–µ–º
      const cloned = response.clone();
      const arrayBuffer = await cloned.arrayBuffer();
      return arrayBuffer.byteLength;
    } catch (error) {
      console.error('[AudioCache] Error getting response size:', error);
      return 0;
    }
  }

  // –£–¥–∞–ª–µ–Ω–∏–µ –∞—É–¥–∏–æ –∏–∑ –∫–µ—à–∞
  async removeAudioFromCache(url) {
    try {
      // –£–¥–∞–ª—è–µ–º –∏–∑ Service Worker –∫–µ—à–∞
      if ('caches' in window) {
        const cache = await caches.open('audio-v1');
        await cache.delete(url);
      }

      // –£–¥–∞–ª—è–µ–º –º–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ –∏–∑ IndexedDB
      await offlineDataService.init();
      const transaction = offlineDataService.getTransaction(['audioFiles'], 'readwrite');
      const store = transaction.objectStore('audioFiles');
      await new Promise((resolve, reject) => {
        const request = store.delete(url);
        request.onsuccess = () => resolve();
        request.onerror = () => reject(request.error);
      });

      console.log('[AudioCache] Removed audio from cache:', url);
      return true;
    } catch (error) {
      console.error('[AudioCache] Error removing audio from cache:', error);
      return false;
    }
  }

  // –ü–æ–ª—É—á–µ–Ω–∏–µ —Å–ø–∏—Å–∫–∞ –∫–µ—à–∏—Ä–æ–≤–∞–Ω–Ω—ã—Ö –∞—É–¥–∏–æ—Ñ–∞–π–ª–æ–≤
  async getCachedAudioList() {
    try {
      await offlineDataService.init();
      const transaction = offlineDataService.getTransaction(['audioFiles']);
      const store = transaction.objectStore('audioFiles');

      return new Promise((resolve, reject) => {
        const request = store.getAll();
        request.onsuccess = () => resolve(request.result);
        request.onerror = () => reject(request.error);
      });
    } catch (error) {
      console.error('[AudioCache] Error getting cached audio list:', error);
      return [];
    }
  }

  // –ü–æ–ª—É—á–µ–Ω–∏–µ –æ–±—â–µ–≥–æ —Ä–∞–∑–º–µ—Ä–∞ –∫–µ—à–∞
  async getCacheSize() {
    try {
      const cachedFiles = await this.getCachedAudioList();
      return cachedFiles.reduce((total, file) => total + (file.size || 0), 0);
    } catch (error) {
      console.error('[AudioCache] Error getting cache size:', error);
      return 0;
    }
  }

  // –û—á–∏—Å—Ç–∫–∞ –≤—Å–µ–≥–æ –∫–µ—à–∞
  async clearCache() {
    try {
      // –û—á–∏—â–∞–µ–º Service Worker –∫–µ—à
      if ('caches' in window) {
        await caches.delete('audio-v1');
      }

      // –û—á–∏—â–∞–µ–º –º–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ –∏–∑ IndexedDB
      await offlineDataService.init();
      const transaction = offlineDataService.getTransaction(['audioFiles'], 'readwrite');
      const store = transaction.objectStore('audioFiles');

      await new Promise((resolve, reject) => {
        const request = store.clear();
        request.onsuccess = () => resolve();
        request.onerror = () => reject(request.error);
      });

      console.log('[AudioCache] Cache cleared');
      return true;
    } catch (error) {
      console.error('[AudioCache] Error clearing cache:', error);
      return false;
    }
  }

  // –ü—Ä–µ–¥–≤–∞—Ä–∏—Ç–µ–ª—å–Ω–æ–µ –∫–µ—à–∏—Ä–æ–≤–∞–Ω–∏–µ –∞—É–¥–∏–æ –¥–ª—è —ç–ø–∏–∑–æ–¥–∞
  async preloadAudio(episodeData) {
    if (!episodeData.audio_url) {
      console.log('[AudioCache] No audio URL for episode:', episodeData.slug);
      return false;
    }

    try {
      return await this.cacheAudio(episodeData.audio_url, episodeData.slug);
    } catch (error) {
      console.error('[AudioCache] Failed to preload audio for episode:', episodeData.slug, error);
      return false;
    }
  }

  // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –≤—Ä–µ–º–µ–Ω–∏ –ø–æ—Å–ª–µ–¥–Ω–µ–≥–æ –¥–æ—Å—Ç—É–ø–∞
  async updateLastAccessed(url) {
    try {
      await offlineDataService.init();
      await offlineDataService.updateAudioFileAccess(url);
    } catch (error) {
      console.error('[AudioCache] Error updating last accessed:', error);
    }
  }

  // –ü–æ–ª—É—á–µ–Ω–∏–µ —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∏ –∫–µ—à–∞
  async getCacheStats() {
    try {
      const cachedFiles = await this.getCachedAudioList();
      const totalSize = cachedFiles.reduce((sum, file) => sum + (file.size || 0), 0);
      const storageEstimate = await offlineDataService.getStorageUsage();

      return {
        fileCount: cachedFiles.length,
        totalSize,
        maxSize: this.maxCacheSize,
        usagePercentage: Math.round((totalSize / this.maxCacheSize) * 100),
        storageQuota: storageEstimate.quota,
        storageUsage: storageEstimate.usage,
        files: cachedFiles.map(file => ({
          url: file.url,
          episodeSlug: file.episode_slug,
          size: file.size,
          cachedAt: file.cached_at,
          lastAccessed: file.last_accessed
        }))
      };
    } catch (error) {
      console.error('[AudioCache] Error getting cache stats:', error);
      return {
        fileCount: 0,
        totalSize: 0,
        maxSize: this.maxCacheSize,
        usagePercentage: 0,
        storageQuota: 0,
        storageUsage: 0,
        files: []
      };
    }
  }
}

// –°–æ–∑–¥–∞–µ–º –µ–¥–∏–Ω—Å—Ç–≤–µ–Ω–Ω—ã–π —ç–∫–∑–µ–º–ø–ª—è—Ä —Å–µ—Ä–≤–∏—Å–∞
const audioCacheService = new AudioCacheService();

export default audioCacheService;

import { supabase } from './supabaseClient';

const logService = {
  log: async (logData) => {
    let userToLog = logData.user;

    if (!userToLog) {
      const { data: { user: currentUserFromAuth } } = await supabase.auth.getUser();
      userToLog = currentUserFromAuth;
    }

    if (!userToLog) {
      console.warn("Log attempt without a user session. Skipping.");
      return { error: new Error("User not available for logging.") };
    }

    const { user, ...restOfLogData } = logData; 

    const payloadToInsert = {
      user_id: userToLog.id,
      user_email: userToLog.email,
      ...restOfLogData,
    };

    delete payloadToInsert.user;

    const { error } = await supabase.from('edit_logs').insert([payloadToInsert]);

    if (error) {
      console.error('Error logging edit:', error);
      return { error };
    }
    return { error: null };
  },

  revert: async (logEntry, user) => {
    if (!logEntry || !user) {
        console.error("Revert call missing log entry or user");
        return { error: new Error("Missing data for revert.") };
    }

    let revertError = null;
    let beforeRevertValue = null;
    let afterRevertValue = logEntry.before_value;

    if (logEntry.entity_type === 'question') {
        const { data: currentData, error: fetchError } = await supabase.from('questions').select('*').eq('id', logEntry.entity_id).single();
        if (fetchError && fetchError.code !== 'PGRST116') { 
          return { error: new Error(`Failed to fetch current question state for revert: ${fetchError.message}`) };
        }
        beforeRevertValue = currentData;

        if (logEntry.action_type === 'create') {
            const { error } = await supabase.from('questions').delete().eq('id', logEntry.entity_id);
            revertError = error;
        } else if (logEntry.action_type === 'update') {
            const { error } = await supabase.from('questions').update(logEntry.before_value).eq('id', logEntry.entity_id);
            revertError = error;
        } else if (logEntry.action_type === 'delete') {
            const { error } = await supabase.from('questions').insert(logEntry.before_value);
            revertError = error;
        }
    } else if (logEntry.entity_type === 'transcript') {
        const { data: currentData, error: fetchError } = await supabase.from('transcripts').select('edited_transcript_data').eq('id', logEntry.entity_id).single();
         if (fetchError) {
          return { error: new Error(`Failed to fetch current transcript state for revert: ${fetchError.message}`) };
        }
        beforeRevertValue = currentData.edited_transcript_data;

        const { error } = await supabase.from('transcripts').update({
            edited_transcript_data: logEntry.before_value
        }).eq('id', logEntry.entity_id);
        revertError = error;
    }

    if (revertError) {
        return { error: revertError };
    }

    const { error: logUpdateError } = await supabase.from('edit_logs').update({
        is_reverted: true,
        reverted_by: user.id,
        reverted_at: new Date().toISOString()
    }).eq('id', logEntry.id);

    if(!logUpdateError) {
        await logService.log({
            user,
            episode_slug: logEntry.episode_slug,
            entity_type: logEntry.entity_type,
            entity_id: logEntry.entity_id,
            action_type: `revert_${logEntry.action_type}`,
            before_value: beforeRevertValue,
            after_value: afterRevertValue,
            is_reverted: false, 
        });
    }

    return { error: logUpdateError };
  }
};

export default logService;

// –°–µ—Ä–≤–∏—Å –¥–ª—è —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –æ—Ñ–ª–∞–π–Ω –¥–∞–Ω–Ω—ã–º–∏ —Å IndexedDB
class OfflineDataService {
  constructor() {
    this.dbName = 'PodcastAppDB';
    this.version = 1;
    this.db = null;
  }

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö
  async init() {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open(this.dbName, this.version);

      request.onerror = () => reject(request.error);

      request.onupgradeneeded = (event) => {
        const db = event.target.result;

        // –•—Ä–∞–Ω–∏–ª–∏—â–µ —ç–ø–∏–∑–æ–¥–æ–≤
        if (!db.objectStoreNames.contains('episodes')) {
          const episodeStore = db.createObjectStore('episodes', { keyPath: 'slug' });
          episodeStore.createIndex('date', 'date');
          episodeStore.createIndex('lang', 'lang');
        }

        // –•—Ä–∞–Ω–∏–ª–∏—â–µ —Ç—Ä–∞–Ω—Å–∫—Ä–∏–ø—Ç–æ–≤
        if (!db.objectStoreNames.contains('transcripts')) {
          const transcriptStore = db.createObjectStore('transcripts', { keyPath: 'id', autoIncrement: true });
          transcriptStore.createIndex('episode_slug', 'episode_slug');
          transcriptStore.createIndex('lang', 'lang');
        }

        // –•—Ä–∞–Ω–∏–ª–∏—â–µ –≤–æ–ø—Ä–æ—Å–æ–≤
        if (!db.objectStoreNames.contains('questions')) {
          const questionStore = db.createObjectStore('questions', { keyPath: 'id', autoIncrement: true });
          questionStore.createIndex('episode_slug', 'episode_slug');
          questionStore.createIndex('lang', 'lang');
          questionStore.createIndex('time', 'time');
        }

        // –•—Ä–∞–Ω–∏–ª–∏—â–µ –∞—É–¥–∏–æ—Ñ–∞–π–ª–æ–≤ (–º–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ)
        if (!db.objectStoreNames.contains('audioFiles')) {
          const audioStore = db.createObjectStore('audioFiles', { keyPath: 'url' });
          audioStore.createIndex('episode_slug', 'episode_slug');
          audioStore.createIndex('cached_at', 'cached_at');
        }

        // –û—á–µ—Ä–µ–¥—å —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏–∏
        if (!db.objectStoreNames.contains('syncQueue')) {
          const syncStore = db.createObjectStore('syncQueue', { keyPath: 'id', autoIncrement: true });
          syncStore.createIndex('type', 'type');
          syncStore.createIndex('timestamp', 'timestamp');
        }

        // –ù–∞—Å—Ç—Ä–æ–π–∫–∏ –∫–µ—à–∞
        if (!db.objectStoreNames.contains('cacheSettings')) {
          const settingsStore = db.createObjectStore('cacheSettings', { keyPath: 'key' });
        }
      };

      request.onsuccess = (event) => {
        this.db = event.target.result;
        resolve(this.db);
      };
    });
  }

  // –ü–æ–ª—É—á–µ–Ω–∏–µ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏
  getTransaction(storeNames, mode = 'readonly') {
    if (!this.db) throw new Error('Database not initialized');
    return this.db.transaction(storeNames, mode);
  }

  // --- –≠–ü–ò–ó–û–î–´ ---

  async saveEpisode(episode) {
    const transaction = this.getTransaction(['episodes'], 'readwrite');
    const store = transaction.objectStore('episodes');

    const episodeData = {
      ...episode,
      cached_at: Date.now(),
      last_updated: Date.now()
    };

    return new Promise((resolve, reject) => {
      const request = store.put(episodeData);
      request.onsuccess = () => resolve(request.result);
      request.onerror = () => reject(request.error);
    });
  }

  async getEpisode(slug) {
    const transaction = this.getTransaction(['episodes']);
    const store = transaction.objectStore('episodes');

    return new Promise((resolve, reject) => {
      const request = store.get(slug);
      request.onsuccess = () => resolve(request.result);
      request.onerror = () => reject(request.error);
    });
  }

  async getAllEpisodes() {
    const transaction = this.getTransaction(['episodes']);
    const store = transaction.objectStore('episodes');

    return new Promise((resolve, reject) => {
      const request = store.getAll();
      request.onsuccess = () => resolve(request.result);
      request.onerror = () => reject(request.error);
    });
  }

  // --- –¢–†–ê–ù–°–ö–†–ò–ü–¢–´ ---

  async saveTranscript(transcript) {
    const transaction = this.getTransaction(['transcripts'], 'readwrite');
    const store = transaction.objectStore('transcripts');

    const transcriptData = {
      ...transcript,
      cached_at: Date.now(),
      last_updated: Date.now()
    };

    return new Promise((resolve, reject) => {
      const request = store.put(transcriptData);
      request.onsuccess = () => resolve(request.result);
      request.onerror = () => reject(request.error);
    });
  }

  async getTranscript(episodeSlug, lang) {
    const transaction = this.getTransaction(['transcripts']);
    const store = transaction.objectStore('transcripts');
    const index = store.index('episode_slug');

    return new Promise((resolve, reject) => {
      const request = index.getAll(episodeSlug);
      request.onsuccess = () => {
        const transcripts = request.result.filter(t => t.lang === lang);
        resolve(transcripts.length > 0 ? transcripts[0] : null);
      };
      request.onerror = () => reject(request.error);
    });
  }

  // --- –í–û–ü–†–û–°–´ ---

  async saveQuestions(questions, episodeSlug, lang) {
    const transaction = this.getTransaction(['questions'], 'readwrite');
    const store = transaction.objectStore('questions');

    // –°–Ω–∞—á–∞–ª–∞ —É–¥–∞–ª—è–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ –≤–æ–ø—Ä–æ—Å—ã –¥–ª—è —ç—Ç–æ–≥–æ —ç–ø–∏–∑–æ–¥–∞ –∏ —è–∑—ã–∫–∞
    await this.deleteQuestions(episodeSlug, lang);

    const promises = questions.map(question => {
      const questionData = {
        ...question,
        episode_slug: episodeSlug,
        lang: lang,
        cached_at: Date.now(),
        last_updated: Date.now()
      };

      return new Promise((resolve, reject) => {
        const request = store.add(questionData);
        request.onsuccess = () => resolve(request.result);
        request.onerror = () => reject(request.error);
      });
    });

    return Promise.all(promises);
  }

  async getQuestions(episodeSlug, lang) {
    const transaction = this.getTransaction(['questions']);
    const store = transaction.objectStore('questions');
    const index = store.index('episode_slug');

    return new Promise((resolve, reject) => {
      const request = index.getAll(episodeSlug);
      request.onsuccess = () => {
        const questions = request.result
          .filter(q => q.lang === lang)
          .sort((a, b) => a.time - b.time);
        resolve(questions);
      };
      request.onerror = () => reject(request.error);
    });
  }

  async deleteQuestions(episodeSlug, lang) {
    const transaction = this.getTransaction(['questions'], 'readwrite');
    const store = transaction.objectStore('questions');
    const index = store.index('episode_slug');

    return new Promise((resolve, reject) => {
      const request = index.getAll(episodeSlug);
      request.onsuccess = () => {
        const questions = request.result.filter(q => q.lang === lang);
        const deletePromises = questions.map(q => {
          return new Promise((deleteResolve, deleteReject) => {
            const deleteRequest = store.delete(q.id);
            deleteRequest.onsuccess = () => deleteResolve();
            deleteRequest.onerror = () => deleteReject(deleteRequest.error);
          });
        });
        Promise.all(deletePromises).then(resolve).catch(reject);
      };
      request.onerror = () => reject(request.error);
    });
  }

  // --- –ê–£–î–ò–û–§–ê–ô–õ–´ ---

  async saveAudioFileMetadata(url, episodeSlug, size = null) {
    const transaction = this.getTransaction(['audioFiles'], 'readwrite');
    const store = transaction.objectStore('audioFiles');

    const audioData = {
      url,
      episode_slug: episodeSlug,
      size,
      cached_at: Date.now(),
      last_accessed: Date.now()
    };

    return new Promise((resolve, reject) => {
      const request = store.put(audioData);
      request.onsuccess = () => resolve(request.result);
      request.onerror = () => reject(request.error);
    });
  }

  async getAudioFileMetadata(url) {
    const transaction = this.getTransaction(['audioFiles']);
    const store = transaction.objectStore('audioFiles');

    return new Promise((resolve, reject) => {
      const request = store.get(url);
      request.onsuccess = () => resolve(request.result);
      request.onerror = () => reject(request.error);
    });
  }

  async updateAudioFileAccess(url) {
    const transaction = this.getTransaction(['audioFiles'], 'readwrite');
    const store = transaction.objectStore('audioFiles');

    return new Promise((resolve, reject) => {
      const getRequest = store.get(url);
      getRequest.onsuccess = () => {
        const audioData = getRequest.result;
        if (audioData) {
          audioData.last_accessed = Date.now();
          const putRequest = store.put(audioData);
          putRequest.onsuccess = () => resolve();
          putRequest.onerror = () => reject(putRequest.error);
        } else {
          resolve();
        }
      };
      getRequest.onerror = () => reject(getRequest.error);
    });
  }

  // --- –û–ß–ï–†–ï–î–¨ –°–ò–ù–•–†–û–ù–ò–ó–ê–¶–ò–ò ---

  async addToSyncQueue(type, data, operation = 'update') {
    const transaction = this.getTransaction(['syncQueue'], 'readwrite');
    const store = transaction.objectStore('syncQueue');

    const syncItem = {
      type, // 'transcript', 'question', 'episode'
      operation, // 'create', 'update', 'delete'
      data,
      timestamp: Date.now(),
      attempts: 0,
      max_attempts: 3
    };

    return new Promise((resolve, reject) => {
      const request = store.add(syncItem);
      request.onsuccess = () => resolve(request.result);
      request.onerror = () => reject(request.error);
    });
  }

  async getSyncQueue() {
    const transaction = this.getTransaction(['syncQueue']);
    const store = transaction.objectStore('syncQueue');

    return new Promise((resolve, reject) => {
      const request = store.getAll();
      request.onsuccess = () => resolve(request.result);
      request.onerror = () => reject(request.error);
    });
  }

  async removeSyncItem(id) {
    const transaction = this.getTransaction(['syncQueue'], 'readwrite');
    const store = transaction.objectStore('syncQueue');

    return new Promise((resolve, reject) => {
      const request = store.delete(id);
      request.onsuccess = () => resolve();
      request.onerror = () => reject(request.error);
    });
  }

  async incrementSyncAttempts(id) {
    const transaction = this.getTransaction(['syncQueue'], 'readwrite');
    const store = transaction.objectStore('syncQueue');

    return new Promise((resolve, reject) => {
      const getRequest = store.get(id);
      getRequest.onsuccess = () => {
        const item = getRequest.result;
        if (item) {
          item.attempts += 1;
          item.last_attempt = Date.now();
          const putRequest = store.put(item);
          putRequest.onsuccess = () => resolve(item);
          putRequest.onerror = () => reject(putRequest.error);
        } else {
          resolve(null);
        }
      };
      getRequest.onerror = () => reject(getRequest.error);
    });
  }

  // --- –ù–ê–°–¢–†–û–ô–ö–ò –ö–ï–®–ê ---

  async saveCacheSetting(key, value) {
    const transaction = this.getTransaction(['cacheSettings'], 'readwrite');
    const store = transaction.objectStore('cacheSettings');

    return new Promise((resolve, reject) => {
      const request = store.put({ key, value, updated_at: Date.now() });
      request.onsuccess = () => resolve();
      request.onerror = () => reject(request.error);
    });
  }

  async getCacheSetting(key, defaultValue = null) {
    const transaction = this.getTransaction(['cacheSettings']);
    const store = transaction.objectStore('cacheSettings');

    return new Promise((resolve, reject) => {
      const request = store.get(key);
      request.onsuccess = () => {
        const result = request.result;
        resolve(result ? result.value : defaultValue);
      };
      request.onerror = () => reject(request.error);
    });
  }

  // --- –û–ß–ò–°–¢–ö–ê –î–ê–ù–ù–´–• ---

  async clearExpiredData(maxAge = 7 * 24 * 60 * 60 * 1000) { // 7 –¥–Ω–µ–π –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é
    const cutoffTime = Date.now() - maxAge;
    const stores = ['episodes', 'transcripts', 'questions', 'audioFiles'];

    for (const storeName of stores) {
      const transaction = this.getTransaction([storeName], 'readwrite');
      const store = transaction.objectStore('store');

      await new Promise((resolve, reject) => {
        const request = store.getAll();
        request.onsuccess = () => {
          const items = request.result;
          const expiredItems = items.filter(item => 
            item.cached_at && item.cached_at < cutoffTime
          );

          const deletePromises = expiredItems.map(item => {
            return new Promise((deleteResolve, deleteReject) => {
              const deleteRequest = store.delete(item.id || item.slug || item.url);
              deleteRequest.onsuccess = () => deleteResolve();
              deleteRequest.onerror = () => deleteReject(deleteRequest.error);
            });
          });

          Promise.all(deletePromises).then(resolve).catch(reject);
        };
        request.onerror = () => reject(request.error);
      });
    }
  }

  async getStorageUsage() {
    if ('storage' in navigator && 'estimate' in navigator.storage) {
      return await navigator.storage.estimate();
    }
    return { usage: 0, quota: 0 };
  }

  // –ó–∞–∫—Ä—ã—Ç–∏–µ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è —Å –±–∞–∑–æ–π –¥–∞–Ω–Ω—ã—Ö
  close() {
    if (this.db) {
      this.db.close();
      this.db = null;
    }
  }
}

// –°–æ–∑–¥–∞–µ–º –µ–¥–∏–Ω—Å—Ç–≤–µ–Ω–Ω—ã–π —ç–∫–∑–µ–º–ø–ª—è—Ä —Å–µ—Ä–≤–∏—Å–∞
const offlineDataService = new OfflineDataService();

export default offlineDataService;

import { 
  S3Client, 
  PutObjectCommand, 
  HeadObjectCommand, 
  DeleteObjectCommand,
  CreateMultipartUploadCommand,
  UploadPartCommand,
  CompleteMultipartUploadCommand,
  AbortMultipartUploadCommand
} from "@aws-sdk/client-s3";
import { getSignedUrl } from '@aws-sdk/s3-request-presigner';
import { getLocaleString } from '@/lib/locales'; 

// Helpers to get env from Vite or fallback to localStorage (dev only)
const getEnv = (key) => {
  try {
    if (typeof import.meta !== 'undefined' && import.meta.env && key in import.meta.env) {
      return import.meta.env[key];
    }
  } catch (_) {}
  try {
    if (typeof window !== 'undefined' && window.localStorage) {
      return window.localStorage.getItem(key) || null;
    }
  } catch (_) {}
  return null;
};

// Primary storage: Selectel S3
const buildPublicBase = (bucket, endpoint) => {
  const ep = (endpoint || '').replace(/^https?:\/\//, '');
  return `https://${bucket}.${ep}`;
};

const R2_PRIMARY_CONFIG = {
  ACCESS_KEY_ID: getEnv('VITE_S3_ACCESS_KEY') || '',
  SECRET_ACCESS_KEY: getEnv('VITE_S3_SECRET_KEY') || '',
  BUCKET: getEnv('VITE_S3_BUCKET') || 'dosmundos-audio',
  ENDPOINT: getEnv('VITE_S3_ENDPOINT') || 'https://s3.kz-1.srvstorage.kz',
  REGION: getEnv('VITE_S3_REGION') || 'kz-1',
  FORCE_PATH_STYLE: false,
  WORKER_PUBLIC_URL: getEnv('VITE_S3_PUBLIC_BASE') || buildPublicBase(getEnv('VITE_S3_BUCKET') || 'dosmundos-audio', getEnv('VITE_S3_ENDPOINT') || 'https://s3.kz-1.srvstorage.kz')
};

// Secondary config disabled
const R2_SECONDARY_CONFIG = null;


const createS3Client = (config) => {
  const endpoint = config.ENDPOINT
    ? config.ENDPOINT
    : `https://${config.ACCOUNT_ID}.${config.ENDPOINT_SUFFIX}`;

  return new S3Client({
    region: config.REGION || "auto",
    endpoint,
    forcePathStyle: Boolean(config.FORCE_PATH_STYLE),
    credentials: {
      accessKeyId: config.ACCESS_KEY_ID,
      secretAccessKey: config.SECRET_ACCESS_KEY,
    },
    // DNS –æ–±—Ö–æ–¥ –∏ —É–ª—É—á—à–µ–Ω–Ω–∞—è –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—è –¥–ª—è –†–æ—Å—Å–∏–∏
    maxAttempts: 3,
    requestHandler: {
      httpOptions: {
        timeout: 30000,
        connectTimeout: 10000,
      }
    },
    customUserAgent: 'DosMundosPodcast/1.0',
  });
};

let primaryS3Client = createS3Client(R2_PRIMARY_CONFIG);
let secondaryS3Client = null;

const r2Service = {
  checkFileExists: async (originalFilename) => {
    const fileKey = originalFilename.replace(/\s+/g, '_');

    const attemptCheck = async (client, config) => {
      try {
        const command = new HeadObjectCommand({ Bucket: config.BUCKET, Key: fileKey });
        await client.send(command);
        const fileUrl = `${config.WORKER_PUBLIC_URL}/${fileKey}`;
        return { exists: true, fileUrl, bucketName: config.BUCKET };
      } catch (error) {
        if (error.name === 'NoSuchKey' || (error.$metadata && error.$metadata.httpStatusCode === 404)) {
          return { exists: false };
        }
        console.warn(`Error checking file in R2 bucket ${config.BUCKET}:`, error);
        return { exists: false, error: error }; 
      }
    };

    const primaryCheck = await attemptCheck(primaryS3Client, R2_PRIMARY_CONFIG);
    if (primaryCheck.exists) return primaryCheck;
    return { exists: false };
  },

  uploadFile: async (file, onProgress, currentLanguage, originalFilename) => {
    const fileKey = originalFilename ? originalFilename.replace(/\s+/g, '_') : `${Date.now()}_${file.name.replace(/\s+/g, '_')}`;
    const totalBytes = file.size || 0;
    const totalMB = totalBytes ? (totalBytes / (1024 * 1024)).toFixed(2) : '0.00';

    const attemptUpload = async (client, config, isPrimaryAttempt) => {
      try {
        if (onProgress) onProgress(0, { stage: 'init', message: '–ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –∑–∞–≥—Ä—É–∑–∫–∏‚Ä¶', uploadedMB: '0.00', totalMB });

        const isIA = !!config.ENDPOINT && config.ENDPOINT.includes('s3.us.archive.org');

        // Route IA uploads via our API for reliable progress + compatibility
        if (isIA && typeof window !== 'undefined') {
          const proxyBaseEnv = getEnv('VITE_IA_PROXY_BASE');
          const isProd = (typeof import.meta !== 'undefined' && import.meta.env && import.meta.env.PROD) || /vercel\.app$/.test(window.location.hostname);
          const base = proxyBaseEnv || (isProd ? '' : null);
          const proxyUrl = base !== null ? `${base}/api/ia-upload?key=${encodeURIComponent(fileKey)}&contentType=${encodeURIComponent(file.type || 'application/octet-stream')}` : null;
          if (proxyUrl) {
            console.log(`[Upload] ${fileKey}: uploading via proxy ${proxyUrl}`);
            let proxyStatus = 0;
            let proxyErrorText = '';
            try {
              await new Promise((resolve, reject) => {
                const xhr = new XMLHttpRequest();
                xhr.open('POST', proxyUrl);
                xhr.responseType = 'json';
                const timeoutMs = 5 * 60 * 1000;
                xhr.timeout = timeoutMs;
                xhr.upload.onprogress = (e) => {
                  if (!e.lengthComputable) return;
                  const percent = Math.floor((e.loaded / Math.max(e.total || totalBytes || 1, 1)) * 100);
                  const uploadedMBNow = ((e.loaded) / (1024 * 1024)).toFixed(2);
                  if (onProgress) onProgress(percent, { stage: 'client-upload', message: '–ó–∞–≥—Ä—É–∑–∫–∞ —Ñ–∞–π–ª–∞‚Ä¶', uploadedMB: uploadedMBNow, totalMB });
                };
                xhr.onload = () => {
                  proxyStatus = xhr.status;
                  if (xhr.status >= 200 && xhr.status < 300) return resolve();
                  proxyErrorText = (xhr.response && xhr.response.error) || xhr.statusText || '';
                  reject(new Error(`Proxy upload failed: ${xhr.status}`));
                };
                xhr.onerror = () => reject(new Error('Network error while uploading to proxy'));
                xhr.ontimeout = () => reject(new Error('Proxy upload timeout'));
                xhr.send(file);
              });
              if (onProgress) onProgress(100, { stage: 'finalizing', message: '–û—Ç–ø—Ä–∞–≤–∫–∞ –Ω–∞ archive.org‚Ä¶', uploadedMB: totalMB, totalMB });
              const fileUrl = `${config.WORKER_PUBLIC_URL}/${fileKey}`;
              console.log(`[Upload] ${fileKey}: completed via proxy. URL: ${fileUrl}`);
              return { fileUrl, fileKey, bucketName: config.BUCKET };
            } catch (proxyErr) {
              console.warn(`[Upload] ${fileKey}: proxy failed (${proxyStatus}) ${proxyErrorText || proxyErr.message}. Falling back to direct upload.`);
              // continue to direct upload below
            }
          }
        }

        // Use browser PUT to pre-signed URL to avoid ReadableStream issues
        let presignedUrl = null;
        const isProd = (typeof import.meta !== 'undefined' && import.meta.env && import.meta.env.PROD);
        if (isProd) {
          const presignResp = await fetch(`/api/s3-presign?key=${encodeURIComponent(fileKey)}&contentType=${encodeURIComponent(file.type || 'application/octet-stream')}`);
          if (!presignResp.ok) throw new Error(`Presign failed: ${presignResp.status}`);
          const json = await presignResp.json();
          presignedUrl = json?.url || null;
        } else {
          // Dev: –ø–æ–¥–ø–∏—Å—ã–≤–∞–µ–º –ø—Ä—è–º–æ –≤ –±—Ä–∞—É–∑–µ—Ä–µ (–∫–ª—é—á–∏ —É–∂–µ –≤ VITE_*)
          const localClient = new S3Client({
            region: R2_PRIMARY_CONFIG.REGION,
            endpoint: R2_PRIMARY_CONFIG.ENDPOINT,
            forcePathStyle: Boolean(R2_PRIMARY_CONFIG.FORCE_PATH_STYLE) === true,
            credentials: {
              accessKeyId: R2_PRIMARY_CONFIG.ACCESS_KEY_ID,
              secretAccessKey: R2_PRIMARY_CONFIG.SECRET_ACCESS_KEY,
            },
          });
          presignedUrl = await getSignedUrl(localClient, new PutObjectCommand({
            Bucket: R2_PRIMARY_CONFIG.BUCKET,
            Key: fileKey,
            ContentType: file.type || 'application/octet-stream',
          }), { expiresIn: 3600 });
        }
        if (!presignedUrl) throw new Error('Empty presigned URL');
        // Use XHR to report progress during upload
        await new Promise((resolve, reject) => {
          const xhr = new XMLHttpRequest();
          xhr.open('PUT', presignedUrl);
          xhr.setRequestHeader('Content-Type', file.type || 'application/octet-stream');
          xhr.upload.onprogress = (e) => {
            const loaded = e.lengthComputable ? e.loaded : 0;
            const percent = Math.floor((loaded / Math.max(totalBytes || 1, 1)) * 100);
            const uploadedMBNow = (loaded / (1024 * 1024)).toFixed(2);
            if (onProgress) onProgress(percent, { stage: 'uploading', message: '–ó–∞–≥—Ä—É–∑–∫–∞ —Ñ–∞–π–ª–∞‚Ä¶', uploadedMB: uploadedMBNow, totalMB });
          };
          xhr.onload = () => {
            if (xhr.status >= 200 && xhr.status < 300) return resolve();
            reject(new Error(`PUT failed: ${xhr.status}`));
          };
          xhr.onerror = () => reject(new Error('Network error during PUT'));
          xhr.send(file);
        });
        if (onProgress) onProgress(100, { stage: 'done', message: '–ì–æ—Ç–æ–≤–æ', uploadedMB: totalMB, totalMB });
        const fileUrl = `${config.WORKER_PUBLIC_URL}/${fileKey}`;
        console.log(`[Upload] ${fileKey}: completed via presigned PUT. URL: ${fileUrl}`);
        return { fileUrl, fileKey, bucketName: config.BUCKET };

      } catch (error) {
        console.error(`Error uploading to bucket ${config.BUCKET}:`, error);
        if (isPrimaryAttempt) {
          console.warn(`Primary upload failed for ${fileKey}. Disabling secondary R2 fallback.`);
          // –í–º–µ—Å—Ç–æ –ø–µ—Ä–µ—Ö–æ–¥–∞ –Ω–∞ R2 secondary, –ø—Ä–æ–±—Ä–æ—Å–∏–º –æ—à–∏–±–∫—É ‚Äî –≤ –†–§ –æ–Ω –Ω–µ–¥–æ—Å—Ç—É–ø–µ–Ω
        }
        throw new Error(getLocaleString('errorUploadingToR2', currentLanguage, {errorMessage: `Bucket ${config.BUCKET}: ${error.message}`}));
      }
    };

    return attemptUpload(primaryS3Client, R2_PRIMARY_CONFIG, true);
  },

  deleteFile: async (fileKey, bucketName, currentLanguage) => {
    const client = primaryS3Client;
    const config = R2_PRIMARY_CONFIG;

    try {
      const command = new DeleteObjectCommand({ Bucket: config.BUCKET, Key: fileKey });
      await client.send(command);
      return { success: true };
    } catch (error) {
