      // Secondary client removed

      // –¢–µ—Å—Ç 3: –ü—Ä–æ–≤–µ—Ä–∫–∞ DNS —Ä–µ–∑–æ–ª–≤–∏–Ω–≥–∞

      try {
        const testUrl = R2_PRIMARY_CONFIG.ENDPOINT
          ? R2_PRIMARY_CONFIG.ENDPOINT
          : `https://${R2_PRIMARY_CONFIG.ACCOUNT_ID}.${R2_PRIMARY_CONFIG.ENDPOINT_SUFFIX}`;
