              if (newWorker.state === 'installed' && navigator.serviceWorker.controller) {
                console.log('[SW] New Service Worker available');
                // –ú–æ–∂–Ω–æ –ø–æ–∫–∞–∑–∞—Ç—å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—é –æ–± –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏
